import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';
import { 
    AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer
} from 'recharts';

function AdminPlaneador() {
    // Agregamos dailyAvg y daysCount al estado inicial
    const [data, setData] = useState({ totalKg: 0, detalle: [], dailyAvg: 0, daysCount: 0 });
    const [chartData, setChartData] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchPlanning();
    }, []);

    const fetchPlanning = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await api.get('/predicciones/planeador-inventario', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            // PROCESAR DATOS PARA GRÁFICA (Agrupar por Fecha)
            const grouped = {};
            response.data.detalle.forEach(p => {
                // Normalizar fecha para evitar duplicados por horas diferentes
                const fechaKey = new Date(p.fecha).toISOString().split('T')[0]; 
                
                // Etiqueta visual para la gráfica
                const label = new Date(p.fecha).toLocaleDateString('es-MX', { month: 'short', day: 'numeric' });
                
                if (!grouped[label]) grouped[label] = 0;
                grouped[label] += p.cantidad;
            });

            // Convertir a array para Recharts
            const chart = Object.keys(grouped).map(key => ({
                fecha: key,
                kg: grouped[key]
            }));

            // --- LÓGICA NUEVA: CALCULAR PROMEDIOS ---
            const totalKg = response.data.totalKg;
            // Contamos las llaves del agrupado para saber cuántos días únicos tienen actividad
            const activeDays = Object.keys(grouped).length || 1; 
            const dailyAvg = (totalKg / activeDays).toFixed(1);

            // Guardamos todo en el estado
            setData({
                ...response.data,
                dailyAvg: dailyAvg,
                daysCount: activeDays
            });
            
            setChartData(chart);

        } catch (error) {
            console.error("Error cargando planeación", error);
        } finally {
            setLoading(false);
        }
    };

    // Estilos
    const containerStyle = { minHeight: '100vh', background: '#f8fafc', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' };
    
    // Contenedor para poner las tarjetas una al lado de la otra
    const statsContainerStyle = { display: 'flex', gap: '20px', flexWrap: 'wrap', width: '100%', maxWidth: 900, marginBottom: 30, justifyContent: 'center' };
    
    const kpiCardStyle = { background: '#fff', padding: 30, borderRadius: 16, boxShadow: '0 4px 6px rgba(0,0,0,0.05)', textAlign: 'center', border: '1px solid #e2e8f0', flex: '1 1 300px', minWidth: 250 };
    
    const graphCardStyle = { background: '#fff', padding: 20, borderRadius: 16, boxShadow: '0 4px 6px rgba(0,0,0,0.05)', border: '1px solid #e2e8f0', width: '100%', maxWidth: 900 };

    return (
        <div style={containerStyle}>
            <div style={{width: '100%', maxWidth: 1000}}>
                <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>⬅ Volver al Panel</button>
                
                <h2 style={{ color: '#1e293b', textAlign: 'center' }}>Planeador de Producción</h2>
                <p style={{ color: '#64748b', textAlign: 'center', marginBottom: 30 }}>
                    Proyección de demanda basada en predicciones pendientes.
                </p>

                {loading ? <p style={{textAlign:'center'}}>Calculando demanda futura...</p> : (
                    <div style={{display:'flex', flexDirection:'column', alignItems:'center'}}>
                        
                        {/* SECCIÓN DE KPIS (Total + Días) */}
                        <div style={statsContainerStyle}>
                            
                            {/* 1. KPI TOTAL */}
                            <div style={kpiCardStyle}>
                                <h3 style={{margin:0, color:'#64748b', fontSize:14, textTransform:'uppercase', letterSpacing:1}}>Necesidad Total</h3>
                                <div style={{fontSize: 48, fontWeight:'bold', color: '#7c3aed', margin:'10px 0'}}>
                                    {data.totalKg} <span style={{fontSize:18, color:'#94a3b8'}}>Kg</span>
                                </div>
                                <div style={{fontSize:12, color:'#64748b'}}>
                                    Para cubrir {data.detalle.length} predicciones.
                                </div>
                            </div>

                            {/* 2. KPI PROMEDIO DIARIO (NUEVO) */}
                            <div style={kpiCardStyle}>
                                <h3 style={{margin:0, color:'#64748b', fontSize:14, textTransform:'uppercase', letterSpacing:1}}>Flujo Diario Promedio</h3>
                                <div style={{fontSize: 48, fontWeight:'bold', color: '#0ea5e9', margin:'10px 0'}}>
                                    {data.dailyAvg} <span style={{fontSize:18, color:'#94a3b8'}}>Kg/día</span>
                                </div>
                                <div style={{fontSize:12, color:'#64748b'}}>
                                    Calculado sobre {data.daysCount} días activos.
                                </div>
                            </div>

                        </div>

                        {/* 3. GRÁFICA DE DEMANDA */}
                        <div style={graphCardStyle}>
                            <h3 style={{color:'#334155', marginBottom:20, textAlign:'center'}}>Cronograma de Demanda</h3>
                            <div style={{ height: 300 }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <AreaChart data={chartData} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                                        <defs>
                                            <linearGradient id="colorKg" x1="0" y1="0" x2="0" y2="1">
                                                <stop offset="5%" stopColor="#8b5cf6" stopOpacity={0.8}/>
                                                <stop offset="95%" stopColor="#8b5cf6" stopOpacity={0}/>
                                            </linearGradient>
                                        </defs>
                                        <XAxis dataKey="fecha" />
                                        <YAxis />
                                        <CartesianGrid strokeDasharray="3 3" vertical={false} />
                                        <Tooltip />
                                        <Area type="monotone" dataKey="kg" stroke="#7c3aed" fillOpacity={1} fill="url(#colorKg)" name="Demanda (Kg)" />
                                    </AreaChart>
                                </ResponsiveContainer>
                            </div>
                        </div>

                        {/* 4. TABLA RESUMEN */}
                        <div style={{marginTop: 30, width: '100%', maxWidth: 900}}>
                            <h4 style={{color: '#475569'}}>Desglose por Cliente</h4>
                            <table style={{width: '100%', borderCollapse: 'collapse', background: '#fff', borderRadius: 8, overflow: 'hidden'}}>
                                <thead style={{background: '#f1f5f9'}}>
                                    <tr>
                                        <th style={{padding:12, textAlign:'left', color:'#475569'}}>Fecha Estimada</th>
                                        <th style={{padding:12, textAlign:'left', color:'#475569'}}>Cliente</th>
                                        <th style={{padding:12, textAlign:'right', color:'#475569'}}>Requerimiento</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.detalle.map(p => (
                                        <tr key={p.id} style={{borderBottom: '1px solid #e2e8f0'}}>
                                            <td style={{padding:12}}>{new Date(p.fecha).toLocaleDateString()}</td>
                                            <td style={{padding:12}}>
                                                <strong>{p.usuario?.username}</strong>
                                                <span style={{fontSize:11, color:'#64748b', marginLeft:5}}>({p.usuario?.perfil?.restaurant || '-'})</span>
                                            </td>
                                            <td style={{padding:12, textAlign:'right', fontWeight:'bold', color:'#7c3aed'}}>
                                                {p.cantidad} Kg
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>

                    </div>
                )}
            </div>
        </div>
    );
}

export default AdminPlaneador;
