import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';
import { 
Â  Â  Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, 
Â  Â  PieChart, Pie, Cell, ComposedChart, Line, Legend, ReferenceLine 
} from 'recharts';

function AdminDesviaciones() {
Â  Â  const [data, setData] = useState([]);
Â  Â  const [stats, setStats] = useState({ stdDev: 0, mean: 0, mape: 0 }); 
Â  Â  const [loading, setLoading] = useState(true);
Â  Â  const navigate = useNavigate();

Â  Â  useEffect(() => {
Â  Â  Â  Â  fetchData();
Â  Â  }, []);

Â  Â  const fetchData = async () => {
Â  Â  Â  Â  try {
Â  Â  Â  Â  Â  Â  const token = localStorage.getItem('token');
Â  Â  Â  Â  Â  Â  const response = await api.get('/predicciones/analisis-desviacion', {
Â  Â  Â  Â  Â  Â  Â  Â  headers: { Authorization: `Bearer ${token}` }
Â  Â  Â  Â  Â  Â  });
Â  Â  Â  Â  Â  Â  
Â  Â  Â  Â  Â  Â  const rawData = response.data;
Â  Â  Â  Â  Â  Â  
Â  Â  Â  Â  Â  Â  const processedData = rawData.map(item => {
Â  Â  Â  Â  Â  Â  Â  Â  // 1. FECHAS
Â  Â  Â  Â  Â  Â  Â  Â  const fechaCreacion = new Date(item.createdAt); 
Â  Â  Â  Â  Â  Â  Â  Â  const fechaPred = new Date(item.fecha); Â  Â  Â  Â  
Â  Â  Â  Â  Â  Â  Â  Â  const fechaReal = new Date(item.pedido.createdAt); 

Â  Â  Â  Â  Â  Â  Â  Â  // DesviaciÃ³n Real en DÃ­as
Â  Â  Â  Â  Â  Â  Â  Â  const diffTime = fechaReal - fechaPred;
Â  Â  Â  Â  Â  Â  Â  Â  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
Â  Â  Â  Â  Â  Â  Â  Â  
Â  Â  Â  Â  Â  Â  Â  Â  // 2. KILOS
Â  Â  Â  Â  Â  Â  Â  Â  const kgPred = item.cantidad;
Â  Â  Â  Â  Â  Â  Â  Â  const kgReal = (item.pedido.solicitudPedido?.grano || 0) + (item.pedido.solicitudPedido?.molido || 0);
Â  Â  Â  Â  Â  Â  Â  Â  const diffKg = kgReal - kgPred;
Â  Â  Â  Â  Â  Â  Â  Â  
