import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';
import { 
    Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, 
    PieChart, Pie, Cell, ComposedChart, Line, Legend, ReferenceLine 
} from 'recharts';

function AdminDesviaciones() {
    // Datos Crudos (Todos)
    const [allData, setAllData] = useState([]);
    // Datos Filtrados (Los que se muestran)
    const [filteredData, setFilteredData] = useState([]);
    
    const [stats, setStats] = useState({ stdDev: 0, mean: 0, mape: 0 }); 
    const [loading, setLoading] = useState(true);
    
    // Filtros de Fecha
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');

    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    // Efecto para filtrar y recalcular estadísticas cuando cambian las fechas
    useEffect(() => {
        filterAndCalculate();
    }, [startDate, endDate, allData]);

    const fetchData = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await api.get('/predicciones/analisis-desviacion', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            const rawData = response.data;
            
            // 1. PROCESAR DATOS INICIALES
            const processed = rawData.map((item, index) => {
                const fechaCreacion = new Date(item.createdAt); 
                const fechaPred = new Date(item.fecha);         
                const fechaReal = new Date(item.pedido.createdAt); 

                const diffTime = fechaReal - fechaPred;
                const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
                
                const kgPred = item.cantidad;
                const kgReal = (item.pedido.solicitudPedido?.grano || 0) + (item.pedido.solicitudPedido?.molido || 0);
                const diffKg = kgReal - kgPred;
                
                const errorKgPct = kgPred !== 0 ? ((diffKg / kgPred) * 100).toFixed(1) : 0;

                const shortDate = new Date(item.fecha).toLocaleDateString('es-MX', {day: '2-digit', month: '2-digit'});
                const uniqueLabel = `${item.usuario?.username} (${shortDate})`;

                return {
                    ...item,
                    cliente: item.usuario?.username || 'Anon',
                    uniqueLabel,
                    kgPred,
                    kgReal,
                    diffDays,
                    diffKg,
                    errorKgPct: parseFloat(errorKgPct),
                    x: index,
                    fechaDate: fechaPred // Guardamos objeto Date para filtrar fácil
                };
            });

            setAllData(processed); // Guardamos copia original
            setFilteredData(processed); // Inicializamos la vista
            setLoading(false);

        } catch (error) {
            console.error("Error cargando análisis", error);
            setLoading(false);
        }
    };

    // LÓGICA DE FILTRADO Y ESTADÍSTICAS
    const filterAndCalculate = () => {
        let dataToUse = allData;

        // 1. Aplicar Filtros de Fecha
        if (startDate || endDate) {
            dataToUse = allData.filter(item => {
                const itemTime = item.fechaDate.getTime();
                const startTime = startDate ? new Date(startDate).getTime() : -Infinity;
                // Al final del día seleccionado
                const endTime = endDate ? new Date(endDate).setHours(23,59,59) : Infinity; 
                
                return itemTime >= startTime && itemTime <= endTime;
            });
        }

        setFilteredData(dataToUse);

        // 2. Recalcular Estadísticas con datos filtrados
        if (dataToUse.length > 0) {
            const diffs = dataToUse.map(d => d.diffKg);
            const mean = diffs.reduce((a, b) => a + b, 0) / diffs.length;
            const variance = diffs.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / diffs.length;
            const stdDev = Math.sqrt(variance);
            const absPctErrors = dataToUse.map(d => Math.abs(d.errorKgPct));
            const mape = absPctErrors.reduce((a, b) => a + b, 0) / absPctErrors.length;

            setStats({ 
                mean: parseFloat(mean.toFixed(2)), 
                stdDev: parseFloat(stdDev.toFixed(2)),
                mape: parseFloat(mape.toFixed(1))
            });
        } else {
            setStats({ mean: 0, stdDev: 0, mape: 0 });
        }
    };

    // 3. CALCULAR TENDENCIA (Solo para la gráfica visible)
    // Se recalcula en cada render para ajustarse a los datos filtrados
    const dataWithTrend = () => {
        const n = filteredData.length;
        if (n <= 1) return filteredData;

        let sumX = 0, sumY = 0, sumXY = 0, sumXX = 0;
        // Re-indexamos X para que la tendencia se dibuje bien en el rango filtrado
        const indexedData = filteredData.map((d, i) => ({ ...d, localX: i }));

        indexedData.forEach(d => {
            sumX += d.localX;
            sumY += d.diffKg;
            sumXY += (d.localX * d.diffKg);
            sumXX += (d.localX * d.localX);
        });

        const slope = (n * sumXY - sumX * sumY) / (n * sumXX - sumX * sumX);
        const intercept = (sumY - slope * sumX) / n;

        return indexedData.map(d => ({
            ...d,
            trend: (slope * d.localX) + intercept
        }));
    };

    // Estilos y Helpers
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden', fontSize: 14 };
    const thStyle = { background: '#334155', color: '#fff', padding: '12px 15px', textAlign: 'left' };
    const tdStyle = { padding: '12px 15px', borderBottom: '1px solid #e2e8f0', color: '#333', verticalAlign: 'middle' };
    const kpiCardStyle = { flex: 1, background: '#f8fafc', padding: 15, borderRadius: 8, textAlign: 'center', border: '1px solid #cbd5e1' };
    const cardGraphStyle = { width: '100%', background: '#fff', borderRadius: 16, border: '2px solid #e5e7eb', padding: 20, marginBottom: 20, boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)' };
    const filterContainerStyle = { display:'flex', gap:15, background:'#fff', padding:15, borderRadius:12, marginBottom:20, boxShadow:'0 2px 5px rgba(0,0,0,0.05)', alignItems:'flex-end' };
    const inputStyle = { padding: 8, borderRadius: 6, border: '1px solid #cbd5e1', fontSize: 14 };

    const getDayColor = (val) => (val === 0 ? '#166534' : '#dc2626');
    const getKgColor = (val) => (val >= 0 ? '#166534' : '#dc2626');

    const getDayStatusBadge = (diffDays) => {
        const absDays = Math.abs(diffDays);
        let text = '', bg = '', color = '';
        if (absDays <= 1) { text = 'En rango'; bg = '#dcfce7'; color = '#166534'; } 
        else if (absDays <= 3) { text = 'Aceptable'; bg = '#fef9c3'; color = '#854d0e'; } 
        else { text = 'Fuera de serie'; bg = '#fee2e2'; color = '#991b1b'; }
        return <span style={{ padding: '4px 10px', borderRadius: '12px', fontWeight: 'bold', fontSize: '12px', background: bg, color: color, display: 'inline-block', whiteSpace: 'nowrap' }}>{text}</span>;
    };

    const getKgBadgeStyle = (val) => {
        const isHigh = Math.abs(val) > 15;
        return { padding: '4px 10px', borderRadius: '12px', fontWeight: 'bold', fontSize: '12px', background: isHigh ? '#fee2e2' : '#dcfce7', color: isHigh ? '#991b1b' : '#166534', display: 'inline-block' };
    };

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            
            <div style={{width: '100%', maxWidth: 1200}}>
                <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>⬅ Volver al Panel</button>
                
                <div style={{display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom: 20}}>
                    <h2 style={{ color: '#1e293b', margin: 0 }}>Estadísticas de Precisión</h2>
                    <span style={{color:'#64748b', fontSize:14}}>Total registros: {filteredData.length}</span>
                </div>

                {/* BARRA DE FILTROS */}
                <div style={filterContainerStyle}>
                    <div>
                        <label style={{display:'block', fontSize:12, fontWeight:'bold', marginBottom:5, color:'#475569'}}>Fecha Inicio</label>
                        <input type="date" style={inputStyle} value={startDate} onChange={(e) => setStartDate(e.target.value)} />
                    </div>
                    <div>
                        <label style={{display:'block', fontSize:12, fontWeight:'bold', marginBottom:5, color:'#475569'}}>Fecha Fin</label>
                        <input type="date" style={inputStyle} value={endDate} onChange={(e) => setEndDate(e.target.value)} />
                    </div>
                    <button 
                        onClick={() => { setStartDate(''); setEndDate(''); }}
                        style={{padding:'8px 15px', background:'#e2e8f0', border:'none', borderRadius:6, cursor:'pointer', fontWeight:'bold', color:'#475569', height:'35px'}}
                    >
                        Limpiar
                    </button>
                </div>

                {loading ? <p>Calculando...</p> : (
                    <>
                        {/* 1. KPIs SUPERIORES (Dinámicos según filtro) */}
                        <div style={{ display: 'flex', gap: 20, marginBottom: 20 }}>
                            <div style={kpiCardStyle}>
                                <h4 style={{margin:0, color:'#64748b'}}>Error Promedio (Kg)</h4>
                                <span style={{fontSize: 28, fontWeight:'bold', color: stats.mean > 0 ? '#166534' : '#dc2626'}}>
                                    {stats.mean > 0 ? '+' : ''}{stats.mean} Kg
                                </span>
                            </div>
                            <div style={kpiCardStyle}>
                                <h4 style={{margin:0, color:'#64748b'}}>Volatilidad (Kg)</h4>
                                <span style={{fontSize: 28, fontWeight:'bold', color: '#475569'}}>±{stats.stdDev}</span>
                            </div>
                            <div style={{...kpiCardStyle, display:'flex', alignItems:'center', justifyContent:'center', gap:15}}>
                                <div style={{height: 80, width: 80}}>
                                    <ResponsiveContainer>
                                        <PieChart>
                                            <Pie data={[{ value: Math.min(stats.mape, 100) }, { value: Math.max(0, 100 - stats.mape) }]} innerRadius={25} outerRadius={35} startAngle={90} endAngle={-270} dataKey="value" stroke="none">
                                                <Cell fill={stats.mape < 15 ? '#22c55e' : '#ef4444'} />
                                                <Cell fill="#e5e7eb" />
                                            </Pie>
                                        </PieChart>
                                    </ResponsiveContainer>
                                </div>
                                <div style={{textAlign:'left'}}>
                                    <h4 style={{margin:0, color:'#64748b'}}>MAPE Global</h4>
                                    <span style={{fontSize: 24, fontWeight:'bold', color: stats.mape < 15 ? '#166534' : '#7f1d1d'}}>{stats.mape}%</span>
                                </div>
                            </div>
                        </div>

                        {/* 2. GRÁFICA */}
                        <div style={cardGraphStyle}>
                            <h3 style={{textAlign:'center', color:'#555', marginBottom:5}}>Desviación en Kilos y Tendencia</h3>
                            <p style={{textAlign:'center', fontSize:12, color:'#888', marginBottom:20}}>
                                Barras: Diferencia Real (Kg) | Línea Naranja: Tendencia Matemática (Filtrada)
                            </p>
                            
                            <div style={{ height: 350 }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <ComposedChart data={dataWithTrend()} margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
                                        <CartesianGrid stroke="#f5f5f5" />
                                        <XAxis dataKey="uniqueLabel" scale="band" style={{fontSize: '12px'}} />
                                        <YAxis label={{ value: 'Desviación (Kg)', angle: -90, position: 'insideLeft' }} />
                                        <Tooltip />
                                        <Legend />
                                        <ReferenceLine y={0} stroke="#000" />
                                        <Bar dataKey="diffKg" name="Diferencia (Kg)" barSize={40}>
                                            {dataWithTrend().map((entry, index) => (
                                                <Cell key={`cell-${index}`} fill={entry.diffKg >= 0 ? '#166534' : '#dc2626'} />
                                            ))}
                                        </Bar>
                                        <Line type="monotone" dataKey="trend" name="Tendencia" stroke="#ff7300" strokeWidth={3} dot={false} strokeDasharray="5 5" />
                                    </ComposedChart>
                                </ResponsiveContainer>
                            </div>
                        </div>

                        {/* 3. TABLA DETALLADA */}
                        <h3 style={{marginTop: 30, color: '#334155'}}>Detalle de Desviaciones</h3>
                        <table style={tableStyle}>
                            <thead>
                                <tr>
                                    <th style={thStyle}>Cliente</th>
                                    <th style={thStyle}>Comparativa</th>
                                    <th style={{...thStyle, background:'#475569'}}>Error Días</th>
                                    <th style={{...thStyle, background:'#475569'}}>Estatus</th>
                                    <th style={{...thStyle, background:'#64748b'}}>Error Kg</th>
                                    <th style={{...thStyle, background:'#64748b'}}>Error % Kg</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredData.map((item) => (
                                    <tr key={item.id} style={{ background: '#fff' }}>
                                        <td style={tdStyle}>
                                            <div style={{fontWeight:'bold'}}>{item.cliente}</div>
                                            <div style={{fontSize:11, color:'#64748b'}}>{item.usuario?.perfil?.restaurant}</div>
                                        </td>
                                        <td style={tdStyle}>
                                            <div style={{fontSize:12}}>🗓️ P: {new Date(item.fecha).toLocaleDateString()}</div>
                                            <div style={{fontSize:12}}>🛒 R: {new Date(item.pedido.createdAt).toLocaleDateString()}</div>
                                            <hr style={{border:'0', borderTop:'1px dashed #eee', margin:'5px 0'}}/>
                                            <div style={{fontSize:12}}>⚖️ P: {item.kgPred} Kg</div>
                                            <div style={{fontSize:12}}>📦 R: {item.kgReal} Kg</div>
                                        </td>
                                        <td style={tdStyle}>
                                            <div style={{fontWeight: 'bold', color: getDayColor(item.diffDays)}}>
                                                {item.diffDays > 0 ? `+${item.diffDays}` : item.diffDays} días
                                            </div>
                                        </td>
                                        <td style={tdStyle}>
                                            {getDayStatusBadge(item.diffDays)}
                                        </td>
                                        <td style={tdStyle}>
                                            <div style={{fontWeight: 'bold', color: getKgColor(item.diffKg)}}>
                                                {item.diffKg > 0 ? '+' : ''}{item.diffKg.toFixed(1)} Kg
                                            </div>
                                        </td>
                                        <td style={tdStyle}>
                                            <span style={getKgBadgeStyle(item.errorKgPct)}>
                                                {item.errorKgPct}%
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </>
                )}
            </div>
        </div>
    );
}

export default AdminDesviaciones;
