import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Login from './pages/Login'; // Ajusta la ruta seg煤n d贸nde lo guardaste
import Bienvenida from './pages/Bienvenida';
import Contacto from './pages/Contacto';
import logoCafe from './assets/logo-cafe-tech.png'
// 隆Paso 1: Importa el archivo CSS Module!
import styles from './Home.module.css';
import SolicitudPedidoForm from './pages/SolicitudPedidoForm';

function Home() {
  return (
    <div className={styles.container}> 
      <nav className={styles.topBar}>
        <Link to="/contacto" className={styles.navLink}>
          Cont谩ctanos
        </Link>
        
        {/*
          CAMBIO AQUI: Ahora es un Link con una imagen dentro.
          La ruta al home (/) es la ruta por defecto para un logo.
        */}
        <Link to="/" className={styles.logoLink}> {/* Envolvemos la imagen en un Link */}
          <img 
            src={logoCafe}// O {coffeeBeanImage} si lo importaste desde src/assets
            alt="Grano de caf茅" 
            className={styles.logo} 
          />
        </Link>
        
        <Link to="/Bienvenida" className={styles.navLink}>
          Iniciar sesi贸n
        </Link>
      </nav>

      <div className={styles.heroSection}>
        <h2>Informaci贸n de la empresa</h2>
        <p>Aqu铆 puedes poner texto corto, descripci贸n y tus servicios principales.</p>
      </div>
    </div>
  );
}

//  Componente para manejar la redirecci贸n de autenticaci贸n
function AuthRedirect({ children }) {
    const navigate = useNavigate();
    const token = localStorage.getItem('token');
    
    useEffect(() => {
        // Si hay un token y estamos en la ruta ra铆z o login, redirige a Bienvenida
        if (token && (window.location.pathname === '/' || window.location.pathname === '/login')) {
            navigate('/Bienvenida', { replace: true });
        }
    }, [token, navigate]);

    // Si hay un token y estamos en Bienvenida o una ruta protegida, renderiza el contenido
    // Si no hay token y estamos en una ruta p煤blica, renderiza el contenido
    return children;
}


function App() {
  return (
    <Router>
      <AuthRedirect>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path='/Bienvenida' element={<Bienvenida />}/>
          <Route path='/Contacto' element={<Contacto />}/>
          <Route path='/solicitud-pedido' element={<SolicitudPedidoForm />}/>
          {/* Aqu铆 podr铆as agregar otras rutas protegidas */}
        </Routes>
      </AuthRedirect>
    </Router>
  );
}

export default App;
