import React, { useState, useEffect } from 'react';
import api from '../api/api';
import { useNavigate, Link } from 'react-router-dom';
import logoCafe from '../assets/logo-cafe-tech.png';

// ---------------------------------------------
// Componente Auxiliar: Tarjeta de Acceso (Adaptado de Admin)
// ---------------------------------------------
const ClientMenuCard = ({ icon, title, desc, onClick, color }) => (
    <div 
        onClick={onClick}
        style={{ 
            background: '#334155', // Gris oscuro
            padding: 20,
            borderRadius: 16,
            width: 180,
            textAlign: 'center', 
            cursor: 'pointer', 
            border: color ? `2px solid ${color}` : '1px solid #475569', 
            transition: 'transform 0.2s', 
            display: 'flex', 
            flexDirection: 'column', 
            alignItems: 'center',
            boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
            minHeight: 180,
            color: '#fff' 
        }}
        onMouseEnter={(e) => {
            e.currentTarget.style.background = '#475569';
            e.currentTarget.style.transform = 'translateY(-5px)';
        }}
        onMouseLeave={(e) => {
            e.currentTarget.style.background = '#334155';
            e.currentTarget.style.transform = 'translateY(0)';
        }}
    >
        <div style={{ fontSize: 42, marginBottom: 10 }}>{icon}</div>
        <div style={{ fontSize: 18, fontWeight: 'bold', color: color || '#a78Bfa', marginBottom: 5 }}>{title}</div>
        <p style={{ fontSize: 13, color: '#cbd5e1', margin: 0 }}>{desc}</p>
    </div>
);

// ---------------------------------------------
// Componente Auxiliar: Widget KPI (Adaptado de Admin)
// ---------------------------------------------
const KPIWidget = ({ title, value, color, icon, onClick, note }) => {
    const [isHovered, setIsHovered] = useState(false);
    return (
        <div 
            style={{
                flex: '1 1 200px', 
                background: '#334155', 
                padding: 20, 
                borderRadius: 16, 
                borderLeft: `6px solid ${color}`, 
                boxShadow: '0 4px 6px rgba(0,0,0,0.2)',
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'space-between',
                color: '#fff',
                cursor: onClick ? 'pointer' : 'default',
                transition: 'transform 0.2s',
                ...(isHovered ? { transform: 'scale(1.03)' } : {})
            }}
            onClick={onClick}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
        >
            <div>
                <div style={{fontSize: 12, color:'#94a3b8', textTransform:'uppercase'}}>{title}</div>
                <div style={{fontSize: 28, fontWeight:'bold', color: value === '...' ? '#cbd5e1' : color}}>{value}</div>
                {note && <div style={{fontSize: 10, color:'#94a3b8', marginTop: 3}}>{note}</div>}
            </div>
            <div style={{fontSize: 30}}>{icon}</div>
        </div>
    );
};


// ---------------------------------------------
// COMPONENTE BIENVENIDA (Dashboard Cliente)
// ---------------------------------------------
function Bienvenida() {
    
    const [profile, setProfile] = useState(null);
    const [dashboardData, setDashboardData] = useState({
        ultimoPedido: '...',
        pedidosPendientes: '...',
        inventario: '...',
        proximoPedido: '...'
    });
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login', { replace: true });
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            handleLogout();
            return;
        }

        const config = { headers: { Authorization: `Bearer ${token}` } };

        // 1. Cargar Perfil
        api.get('/profile/me', config)
            .then(res => setProfile(res.data))
            .catch(err => console.error('Error perfil', err));

        // 2. Cargar Dashboard (Mantiene la funcionalidad)
        const fetchData = async () => {
            try {
                const [pedidosRes, invRes, predRes] = await Promise.allSettled([
                    api.get('/pedidos/me', config),
                    api.get('/inventario/me', config),
                    api.get('/predicciones/me', config)
                ]);
                
                let ultimo = 'Sin datos';
                let pendientes = 0;
                if (pedidosRes.status === 'fulfilled') {
                    const pedidos = pedidosRes.value.data;
                    pendientes = pedidos.filter(p => !p.entregado).length;
                    if (pedidos.length > 0) {
                        const sortedPedidos = pedidos.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
                        const fechaUltimo = new Date(sortedPedidos[0].createdAt);
                        ultimo = fechaUltimo.toLocaleDateString(); 
                    }
                }
                
                let stock = '0 Kg';
                if (invRes.status === 'fulfilled' && invRes.value.data) {
                    stock = `${invRes.value.data.cantidad} Kg`;
                }
                
                let proximo = 'No calculado';
                if (predRes.status === 'fulfilled') {
                    const preds = predRes.value.data;
                    if (preds.length > 0) {
                        const fechaPred = new Date(preds[0].fecha);
                        proximo = fechaPred.toLocaleDateString();
                    }
                }
                setDashboardData({ ultimoPedido: ultimo, pedidosPendientes: pendientes, inventario: stock, proximoPedido: proximo });
            } catch (error) {
                console.error("Error cargando dashboard", error);
            }
        };
        fetchData();
    }, [navigate]);

    const username = profile ? (profile.firstName && profile.lastName ? `${profile.firstName} ${profile.lastName}` : profile.username) : 'Cliente';

    // --- ESTILOS ADAPTADOS DEL ADMIN DASHBOARD ---
    const CONTAINER_STYLE = { minHeight: '100vh', background: '#1e293b', color: '#fff', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' };
    const GRID_STYLE = { display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: 20, maxWidth: 1000, width: '100%' };
    
    return (
        <div style={CONTAINER_STYLE}>
            
            {/* HEADER ADAPTADO */}
            <div style={{ width: '100%', maxWidth: 1000, display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 30 }}>
                <h1 style={{ color: '#a78Bfa', margin: 0, fontSize: 24 }}>Bienvenido, {username}</h1>
                <div style={{ display: 'flex', gap: 15, alignItems: 'center' }}>
                    <Link to="/contacto" style={{ background: '#7dd3fc', color: '#222', padding: '8px 16px', borderRadius: 8, border: 'none', cursor: 'pointer', fontWeight: '600', textDecoration: 'none' }}>Soporte</Link>
                    <button onClick={handleLogout} style={{ background: '#ef4444', color: '#fff', padding: '8px 16px', borderRadius: 8, border: 'none', cursor: 'pointer' }}>Salir</button>
                </div>
            </div>

            {/* 1. GRID DE MEN (Accesos R谩pidos) - AHORA ES PRIMERO */}
            <h2 style={{color: '#a78Bfa', alignSelf: 'center', marginBottom: 20, fontSize: 18}}>Accesos R谩pidos</h2>
            <div style={{...GRID_STYLE, marginBottom: 40}}>
                
                <ClientMenuCard 
                    icon="" 
                    title="Hacer Pedido" 
                    desc="Solicita tu nuevo env铆o de caf茅." 
                    onClick={() => navigate('/solicitud-pedido')} 
                    color="#10b981" 
                />
                <ClientMenuCard 
                    icon="" 
                    title="An谩lisis de Negocio" 
                    desc="Estad铆sticas de tu consumo." 
                    onClick={() => navigate('/analisis-negocio')} 
                    color="#3b82f6" 
                />
                <ClientMenuCard 
                    icon="" 
                    title="Historial de Pedidos" 
                    desc="Revisa tus pedidos anteriores." 
                    onClick={() => navigate('/pedidos-anteriores')} 
                    color="#a78Bfa" 
                />
                <ClientMenuCard 
                    icon="" 
                    title="Pagos y Saldo" 
                    desc="Consulta tu estado de cuenta." 
                    onClick={() => navigate('/pagos')} 
                    color="#f59e0b" 
                />
                <ClientMenuCard 
                    icon="" 
                    title="Predicciones" 
                    desc="Consulta tu demanda futura." 
                    onClick={() => navigate('/predicciones')} 
                    color="#c084fc" 
                />
