import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

function AdminPlaneador() {
    const [data, setData] = useState({ totalKg: 0, detalle: [] });
    const [chartData, setChartData] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPlanning = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await api.get('/predicciones/planeador-inventario', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                
                setData(response.data);

                // Agrupar por fecha para la gráfica
                const grouped = {};
                response.data.detalle.forEach(p => {
                    const fecha = new Date(p.fecha).toLocaleDateString('es-MX', { month: 'short', day: 'numeric' });
                    if (!grouped[fecha]) grouped[fecha] = 0;
                    grouped[fecha] += p.cantidad;
                });

                const chart = Object.keys(grouped).map(key => ({
                    fecha: key,
                    kg: grouped[key]
                }));
                setChartData(chart);

            } catch (error) {
                console.error("Error cargando planeador", error);
            } finally {
                setLoading(false);
            }
        };
        fetchPlanning();
    }, []);

    // Estilos
    const containerStyle = { minHeight: '100vh', background: '#f1f5f9', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' };
    const kpiCardStyle = { background: '#fff', padding: 30, borderRadius: 16, boxShadow: '0 4px 6px rgba(0,0,0,0.05)', textAlign: 'center', border: '1px solid #e2e8f0', width: '100%', maxWidth: 400, marginBottom: 20 };
    const graphCardStyle = { background: '#fff', padding: 20, borderRadius: 16, boxShadow: '0 4px 6px rgba(0,0,0,0.05)', border: '1px solid #e2e8f0', width: '100%', maxWidth: 900, marginBottom: 20 };
    const tableStyle = { width: '100%', borderCollapse: 'collapse', background: '#fff', borderRadius: 8, overflow: 'hidden', fontSize: 14 };

    return (
        <div style={containerStyle}>
            <div style={{width: '100%', maxWidth: 1000}}>
                <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>⬅ Volver al Panel</button>
                
                <h2 style={{ color: '#1e293b', textAlign: 'center', marginBottom: 30 }}>Planeador de Producción</h2>

                {loading ? <p style={{textAlign:'center'}}>Calculando demanda...</p> : (
                    <div style={{display:'flex', flexDirection:'column', alignItems:'center'}}>
                        
                        {/* 1. KPI TOTAL NECESARIO */}
                        <div style={kpiCardStyle}>
                            <h3 style={{margin:0, color:'#64748b', fontSize:14, textTransform:'uppercase'}}>Producción Requerida</h3>
                            <div style={{fontSize: 56, fontWeight:'bold', color: '#7c3aed', margin:'10px 0'}}>
                                {data.totalKg} <span style={{fontSize:20, color:'#94a3b8'}}>Kg</span>
                            </div>
                            <div style={{fontSize:12, color:'#64748b'}}>Basado en {data.detalle.length} predicciones pendientes.</div>
                        </div>

                        {/* 2. GRÁFICA DE DEMANDA */}
                        <div style={graphCardStyle}>
                            <h3 style={{color:'#334155', marginBottom:10, textAlign:'center', fontSize: 16}}>Cronograma de Demanda Estimada</h3>
                            <div style={{ height: 250 }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <AreaChart data={chartData} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                                        <defs>
                                            <linearGradient id="colorKg" x1="0" y1="0" x2="0" y2="1">
                                                <stop offset="5%" stopColor="#8b5cf6" stopOpacity={0.8}/>
                                                <stop offset="95%" stopColor="#8b5cf6" stopOpacity={0}/>
                                            </linearGradient>
                                        </defs>
                                        <XAxis dataKey="fecha" />
                                        <YAxis />
                                        <CartesianGrid strokeDasharray="3 3" vertical={false} />
                                        <Tooltip />
                                        <Area type="monotone" dataKey="kg" stroke="#7c3aed" fillOpacity={1} fill="url(#colorKg)" />
                                    </AreaChart>
                                </ResponsiveContainer>
                            </div>
                        </div>

                        {/* 3. TABLA DE DETALLE */}
                        <div style={{width: '100%', maxWidth: 900}}>
                            <h4 style={{color: '#475569', marginBottom: 10}}>Detalle por Cliente</h4>
                            <table style={tableStyle}>
                                <thead style={{background: '#334155', color: '#fff'}}>
                                    <tr>
                                        <th style={{padding:12, textAlign:'left'}}>Fecha Estimada</th>
                                        <th style={{padding:12, textAlign:'left'}}>Cliente</th>
                                        <th style={{padding:12, textAlign:'right'}}>Cantidad</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.detalle.map(p => (
                                        <tr key={p.id} style={{borderBottom: '1px solid #e2e8f0'}}>
                                            <td style={{padding:12}}>{new Date(p.fecha).toLocaleDateString()}</td>
                                            <td style={{padding:12}}>
                                                <strong>{p.usuario?.username}</strong>
                                                <span style={{fontSize:11, color:'#64748b', marginLeft:5}}>({p.usuario?.perfil?.restaurant || '-'})</span>
                                            </td>
                                            <td style={{padding:12, textAlign:'right', fontWeight:'bold', color:'#7c3aed'}}>
                                                {p.cantidad} Kg
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>

                    </div>
                )}
            </div>
        </div>
    );
}

export default AdminPlaneador;
