import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';
import { 
    Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, 
    PieChart, Pie, Cell, ComposedChart, Line, Legend, ReferenceLine 
} from 'recharts';

function AdminDesviaciones() {
    const [data, setData] = useState([]);
    const [stats, setStats] = useState({ stdDev: 0, mean: 0, mape: 0 }); 
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await api.get('/predicciones/analisis-desviacion', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            const rawData = response.data;
            
            const processedData = rawData.map(item => {
                // 1. FECHAS
                const fechaCreacion = new Date(item.createdAt); 
                const fechaPred = new Date(item.fecha);         
                const fechaReal = new Date(item.pedido.createdAt); 

                // Desviaci√≥n D√≠as
                const diffTime = fechaReal - fechaPred;
                const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
                
                // Desviaci√≥n Kilos
                const kgPred = item.cantidad;
                const kgReal = (item.pedido.solicitudPedido?.grano || 0) + (item.pedido.solicitudPedido?.molido || 0);
                const diffKg = kgReal - kgPred;
                
                // Error % Kg
                const errorKgPct = kgPred !== 0 ? ((diffKg / kgPred) * 100).toFixed(1) : 0;

                // Etiqueta √∫nica para el eje X de la gr√°fica
                const shortDate = new Date(item.fecha).toLocaleDateString('es-MX', {day: '2-digit', month: '2-digit'});
                const uniqueLabel = `${item.usuario?.username} (${shortDate})`;

                return {
                    ...item,
                    cliente: item.usuario?.username || 'Anon',
                    uniqueLabel,
                    kgPred,
                    kgReal,
                    diffDays,
                    diffKg,
                    errorKgPct: parseFloat(errorKgPct)
                };
            });

            // Estad√≠sticas
            if (processedData.length > 0) {
                const diffs = processedData.map(d => d.diffKg);
                const mean = diffs.reduce((a, b) => a + b, 0) / diffs.length;
                const variance = diffs.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / diffs.length;
                const stdDev = Math.sqrt(variance);
                const absPctErrors = processedData.map(d => Math.abs(d.errorKgPct));
                const mape = absPctErrors.reduce((a, b) => a + b, 0) / absPctErrors.length;

                setStats({ 
                    mean: parseFloat(mean.toFixed(2)), 
                    stdDev: parseFloat(stdDev.toFixed(2)),
                    mape: parseFloat(mape.toFixed(1))
                });
            }

            setData(processedData);

        } catch (error) {
            console.error("Error cargando an√°lisis", error);
        } finally {
            setLoading(false);
        }
    };

    // Estilos
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden', fontSize: 14 };
    const thStyle = { background: '#334155', color: '#fff', padding: '12px 15px', textAlign: 'left' };
    const tdStyle = { padding: '12px 15px', borderBottom: '1px solid #e2e8f0', color: '#333', verticalAlign: 'middle' };
    const kpiCardStyle = { flex: 1, background: '#f8fafc', padding: 15, borderRadius: 8, textAlign: 'center', border: '1px solid #cbd5e1' };
    const cardGraphStyle = { width: '100%', background: '#fff', borderRadius: 16, border: '2px solid #e5e7eb', padding: 20, marginBottom: 20, boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)' };

    // Helpers de estilo
    const getDayColor = (val) => (val === 0 ? '#166534' : '#dc2626');
    const getKgColor = (val) => (val >= 0 ? '#166534' : '#dc2626');

    const getDayStatusBadge = (diffDays) => {
        const absDays = Math.abs(diffDays);
        let text = '', bg = '', color = '';
        if (absDays <= 1) { text = 'En rango'; bg = '#dcfce7'; color = '#166534'; } 
        else if (absDays <= 3) { text = 'Aceptable'; bg = '#fef9c3'; color = '#854d0e'; } 
        else { text = 'Fuera de serie'; bg = '#fee2e2'; color = '#991b1b'; }
        return <span style={{ padding: '4px 10px', borderRadius: '12px', fontWeight: 'bold', fontSize: '12px', background: bg, color: color, display: 'inline-block', whiteSpace: 'nowrap' }}>{text}</span>;
    };

    const getKgBadgeStyle = (val) => {
        const isHigh = Math.abs(val) > 15;
        return { padding: '4px 10px', borderRadius: '12px', fontWeight: 'bold', fontSize: '12px', background: isHigh ? '#fee2e2' : '#dcfce7', color: isHigh ? '#991b1b' : '#166534', display: 'inline-block' };
    };

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            
            <div style={{width: '100%', maxWidth: 1200}}>
                <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>‚¨Ö Volver al Panel</button>
                
                <h2 style={{ color: '#1e293b' }}>Estad√≠sticas de Precisi√≥n</h2>

                {loading ? <p>Calculando...</p> : (
                    <>
                        {/* 1. KPIs */}
                        <div style={{ display: 'flex', gap: 20, marginBottom: 20 }}>
                            <div style={kpiCardStyle}>
                                <h4 style={{margin:0, color:'#64748b'}}>Error Promedio (Kg)</h4>
                                <span style={{fontSize: 28, fontWeight:'bold', color: stats.mean > 0 ? '#166534' : '#dc2626'}}>{stats.mean > 0 ? '+' : ''}{stats.mean} Kg</span>
                            </div>
                            <div style={kpiCardStyle}>
                                <h4 style={{margin:0, color:'#64748b'}}>Volatilidad (Kg)</h4>
                                <span style={{fontSize: 28, fontWeight:'bold', color: '#475569'}}>¬±{stats.stdDev}</span>
                            </div>
                            <div style={{...kpiCardStyle, display:'flex', alignItems:'center', justifyContent:'center', gap:15}}>
                                <div style={{height: 80, width: 80}}>
                                    <ResponsiveContainer>
                                        <PieChart>
                                            <Pie data={[{ value: Math.min(stats.mape, 100) }, { value: Math.max(0, 100 - stats.mape) }]} innerRadius={25} outerRadius={35} startAngle={90} endAngle={-270} dataKey="value" stroke="none">
                                                <Cell fill={stats.mape < 15 ? '#22c55e' : '#ef4444'} />
                                                <Cell fill="#e5e7eb" />
                                            </Pie>
                                        </PieChart>
                                    </ResponsiveContainer>
                                </div>
                                <div style={{textAlign:'left'}}>
                                    <h4 style={{margin:0, color:'#64748b'}}>MAPE Global</h4>
                                    <span style={{fontSize: 24, fontWeight:'bold', color: stats.mape < 15 ? '#166534' : '#7f1d1d'}}>{stats.mape}%</span>
                                </div>
                            </div>
                        </div>

                        {/* 2. GR√ÅFICA (DOBLE EJE: KG vs %) */}
                        <div style={cardGraphStyle}>
                            <h3 style={{textAlign:'center', color:'#555', marginBottom:5}}>Desviaci√≥n Real vs Error %</h3>
                            <p style={{textAlign:'center', fontSize:12, color:'#888', marginBottom:20}}>
                                Barras: Diferencia en Kg (Eje Izq) | L√≠nea: Porcentaje de Error (Eje Der)
                            </p>
                            
                            <div style={{ height: 350 }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <ComposedChart data={data} margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
                                        <CartesianGrid stroke="#f5f5f5" />
                                        {/* Usamos uniqueLabel para evitar errores de visualizaci√≥n */}
                                        <XAxis dataKey="uniqueLabel" scale="band" style={{fontSize:'12px'}} />
                                        
                                        <YAxis yAxisId="left" label={{ value: 'Desviaci√≥n (Kg)', angle: -90, position: 'insideLeft' }} />
                                        <YAxis yAxisId="right" orientation="right" label={{ value: 'Error %', angle: 90, position: 'insideRight' }} />
                                        
                                        <Tooltip />
                                        <Legend />
                                        <ReferenceLine yAxisId="left" y={0} stroke="#000" />

                                        <Bar yAxisId="left" dataKey="diffKg" name="Diferencia (Kg)" barSize={40}>
                                            {data.map((entry, index) => (
                                                <Cell key={`cell-${index}`} fill={entry.diffKg >= 0 ? '#166534' : '#dc2626'} />
                                            ))}
                                        </Bar>

                                        <Line yAxisId="right" type="monotone" dataKey="errorKgPct" name="Error %" stroke="#ff7300" strokeWidth={3} dot={{r: 4}} />
                                    </ComposedChart>
                                </ResponsiveContainer>
                            </div>
                        </div>

                        {/* 3. TABLA DETALLADA (CON ETIQUETAS DE ESTATUS) */}
                        <h3 style={{marginTop: 30, color: '#334155'}}>Detalle de Desviaciones</h3>
                        <table style={tableStyle}>
                            <thead>
                                <tr>
                                    <th style={thStyle}>Cliente</th>
                                    <th style={thStyle}>Comparativa</th>
                                    <th style={{...thStyle, background:'#475569'}}>Error D√≠as</th>
                                    <th style={{...thStyle, background:'#475569'}}>Estatus</th>
                                    <th style={{...thStyle, background:'#64748b'}}>Error Kg</th>
                                    <th style={{...thStyle, background:'#64748b'}}>Error % Kg</th>
                                </tr>
                            </thead>
                            <tbody>
                                {data.map((item) => (
                                    <tr key={item.id} style={{ background: '#fff' }}>
                                        <td style={tdStyle}>
                                            <div style={{fontWeight:'bold'}}>{item.usuario?.username}</div>
                                            <div style={{fontSize:11, color:'#64748b'}}>{item.usuario?.perfil?.restaurant}</div>
                                        </td>
                                        <td style={tdStyle}>
                                            <div style={{fontSize:12}}>üóìÔ∏è P: {new Date(item.fecha).toLocaleDateString()}</div>
                                            <div style={{fontSize:12}}>üõí R: {new Date(item.pedido.createdAt).toLocaleDateString()}</div>
                                            <hr style={{border:'0', borderTop:'1px dashed #eee', margin:'5px 0'}}/>
                                            <div style={{fontSize:12}}>‚öñÔ∏è P: {item.kgPred} Kg</div>
                                            <div style={{fontSize:12}}>üì¶ R: {item.kgReal} Kg</div>
                                        </td>
                                        <td style={tdStyle}>
                                            <div style={{fontWeight: 'bold', color: getDayColor(item.diffDays)}}>
                                                {item.diffDays > 0 ? `+${item.diffDays}` : item.diffDays} d√≠as
                                            </div>
                                        </td>
                                        <td style={tdStyle}>
                                            {getDayStatusBadge(item.diffDays)}
                                        </td>
                                        <td style={tdStyle}>
                                            <div style={{fontWeight: 'bold', color: getKgColor(item.diffKg)}}>
                                                {item.diffKg > 0 ? '+' : ''}{item.diffKg.toFixed(1)} Kg
                                            </div>
                                        </td>
                                        <td style={tdStyle}>
                                            <span style={getKgBadgeStyle(item.errorKgPct)}>
                                                {item.errorKgPct}%
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </>
                )}
            </div>
        </div>
    );
}

export default AdminDesviaciones;
