// src/pages/admin/AdminDashboard.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

function AdminDashboard() {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login', { replace: true });
    };

    // Estilos
    const containerStyle = { minHeight: '100vh', background: '#1e293b', color: '#fff', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' };
    const cardStyle = { background: '#334155', padding: 30, borderRadius: 12, width: 300, textAlign: 'center', margin: 15, cursor: 'pointer', border: '1px solid #475569', transition: '0.3s' };
    const titleStyle = { fontSize: 24, fontWeight: 'bold', color: '#a78Bfa', marginBottom: 10 };

    // Helper para efectos hover
    const handleMouseEnter = (e) => e.currentTarget.style.background = '#475569';
    const handleMouseLeave = (e) => e.currentTarget.style.background = '#334155';

    return (
        <div style={containerStyle}>
            <div style={{ width: '100%', maxWidth: 800, display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 40 }}>
                <h1 style={{ color: '#a78Bfa' }}>Panel de Administrador</h1>
                <button onClick={handleLogout} style={{ background: '#ef4444', color: '#fff', padding: '10px 20px', borderRadius: 8, border: 'none', cursor: 'pointer' }}>Cerrar SesiÃ³n</button>
            </div>

            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                
                {/* 1. SOLICITUDES (Entrada de nuevos pedidos) */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/solicitudes')}
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>ðŸ“©</div>
                    <div style={titleStyle}>Solicitudes</div>
                    <p>Revisar y aprobar nuevas solicitudes de clientes.</p>
                </div>

                {/* 2. PEDIDOS ACTIVOS (Seguimiento) */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/pedidos')}
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>ðŸ“¦</div>
                    <div style={titleStyle}>Pedidos Activos</div>
                    <p>Controlar entregas y pagos de pedidos ya creados.</p>
                </div>

                {/* 3. Clientes (Futuro) */}
                <div style={{...cardStyle, opacity: 0.7}}>
                    <div style={{ fontSize: 40, marginBottom: 10 }}>ðŸ‘¥</div>
                    <div style={titleStyle}>Clientes</div>
                    <p>Ver lista de usuarios y perfiles.</p>
                </div>

                {/* 4. Pagos (Futuro) */}
                <div style={{...cardStyle, opacity: 0.7}}>
                    <div style={{ fontSize: 40, marginBottom: 10 }}>ðŸ’°</div>
                    <div style={titleStyle}>Pagos Globales</div>
                    <p>Historial de ingresos y deudas.</p>
                </div>

            </div>
        </div>
    );
}

export default AdminDashboard;
