import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';

function AdminInventario() {
    const [inventarios, setInventarios] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchInventarioGlobal();
    }, []);

    const fetchInventarioGlobal = async () => {
        try {
            const token = localStorage.getItem('token');
            // Llamamos al endpoint que trae todos los inventarios
            const response = await api.get('/inventario', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            // Ordenamos por cantidad (los que tienen MENOS primero, para ver urgencias)
            const sorted = response.data.sort((a, b) => a.cantidad - b.cantidad);
            setInventarios(sorted);

        } catch (error) {
            console.error("Error cargando inventario", error);
        } finally {
            setLoading(false);
        }
    };

    // Estilos
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden', fontSize: 14 };
    const thStyle = { background: '#334155', color: '#fff', padding: '12px 15px', textAlign: 'left' };
    const tdStyle = { padding: '12px 15px', borderBottom: '1px solid #e2e8f0', color: '#333', verticalAlign: 'middle' };

    // Helper para alerta visual de stock bajo
    const getStockStatus = (kg) => {
        if (kg <= 5) return { color: '#dc2626', bg: '#fee2e2', label: 'CRÍTICO' }; // Menos de 5kg
        if (kg <= 10) return { color: '#d97706', bg: '#fef3c7', label: 'BAJO' };   // Menos de 10kg
        return { color: '#166534', bg: '#dcfce7', label: 'OK' };
    };

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20 }}>
            <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>⬅ Volver al Panel</button>
            
            <h2 style={{ color: '#1e293b' }}>Monitor de Inventario Global</h2>
            <p style={{ color: '#64748b' }}>Estado actual del stock en posesión de los clientes.</p>

            {loading ? <p>Cargando...</p> : (
                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th style={thStyle}>Cliente</th>
                            <th style={thStyle}>Stock Actual</th>
                            <th style={thStyle}>Estatus</th>
                            <th style={thStyle}>Registro Anterior</th>
                            <th style={thStyle}>Última Actualización</th>
                        </tr>
                    </thead>
                    <tbody>
                        {inventarios.map((inv) => {
                            const status = getStockStatus(inv.cantidad);
                            
                            return (
                                <tr key={inv.id} style={{ background: '#fff' }}>
                                    <td style={tdStyle}>
                                        <div style={{fontWeight:'bold'}}>{inv.usuario?.username || 'Desconocido'}</div>
                                        {/* Nota: Si el backend no trae el perfil anidado, solo veremos el username */}
                                        <div style={{fontSize:11, color:'#64748b'}}>{inv.usuario?.email}</div>
                                    </td>
                                    
                                    <td style={tdStyle}>
                                        <span style={{fontSize: 16, fontWeight: 'bold', color: '#0f172a'}}>
                                            {inv.cantidad} Kg
                                        </span>
                                    </td>

                                    <td style={tdStyle}>
                                        <span style={{
                                            background: status.bg, color: status.color,
                                            padding: '4px 8px', borderRadius: 12, fontSize: 11, fontWeight: 'bold'
                                        }}>
                                            {status.label}
                                        </span>
                                    </td>

                                    <td style={tdStyle}>
                                        <span style={{color: '#64748b'}}>{inv.ultimaCantidad} Kg</span>
                                    </td>

                                    <td style={tdStyle}>
                                        {new Date(inv.updatedAt).toLocaleDateString()} 
                                        <span style={{fontSize:10, color:'#999', marginLeft:5}}>
                                            {new Date(inv.updatedAt).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                                        </span>
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            )}
            
            {!loading && inventarios.length === 0 && (
                <p style={{textAlign:'center', marginTop:20, color:'#666'}}>No hay registros de inventario.</p>
            )}
        </div>
    );
}

export default AdminInventario;
