import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';

function AdminDesviaciones() {
    const [data, setData] = useState([]);
    const [stats, setStats] = useState({ stdDev: 0, mean: 0, mape: 0 }); 
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await api.get('/predicciones/analisis-desviacion', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            const rawData = response.data;
            
            const processedData = rawData.map(item => {
                const kgPred = item.cantidad;
                const kgReal = (item.pedido.solicitudPedido?.grano || 0) + (item.pedido.solicitudPedido?.molido || 0);
                const diffKg = kgReal - kgPred;
                // Calcular porcentaje de error (basado en Kg)
                const errorPct = kgPred !== 0 ? ((diffKg / kgPred) * 100).toFixed(1) : 0;

                return {
                    ...item,
                    cliente: item.usuario?.username || 'Anon',
                    kgPred,
                    kgReal,
                    diffKg,
                    errorPct: parseFloat(errorPct)
                };
            });

            if (processedData.length > 0) {
                const diffs = processedData.map(d => d.diffKg);
                const mean = diffs.reduce((a, b) => a + b, 0) / diffs.length;
                const variance = diffs.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / diffs.length;
                const stdDev = Math.sqrt(variance);
                
                const absPctErrors = processedData.map(d => Math.abs(d.errorPct));
                const mape = absPctErrors.reduce((a, b) => a + b, 0) / absPctErrors.length;

                setStats({ 
                    mean: parseFloat(mean.toFixed(2)), 
                    stdDev: parseFloat(stdDev.toFixed(2)),
                    mape: parseFloat(mape.toFixed(1))
                });
            }

            setData(processedData);

        } catch (error) {
            console.error("Error cargando an√°lisis", error);
        } finally {
            setLoading(false);
        }
    };

    // Estilos
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden', fontSize: 14 };
    const thStyle = { background: '#334155', color: '#fff', padding: '12px 15px', textAlign: 'left' };
    const tdStyle = { padding: '12px 15px', borderBottom: '1px solid #e2e8f0', color: '#333', verticalAlign: 'middle' };
    const kpiCardStyle = { flex: 1, background: '#f8fafc', padding: 15, borderRadius: 8, textAlign: 'center', border: '1px solid #cbd5e1' };
    const cardGraphStyle = { width: '100%', background: '#fff', borderRadius: 16, border: '2px solid #e5e7eb', padding: 20, marginBottom: 20, boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)' };

    // Helper para color de D√≠as
    const getDayColor = (val) => {
        if (val === 0) return '#166534';
        return '#dc2626'; // Rojo para cualquier atraso/adelanto significativo
    };

    // Helper para color de Kilos
    const getKgColor = (val) => {
        return val >= 0 ? '#166534' : '#dc2626';
    };

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            
            <div style={{width: '100%', maxWidth: 1100}}>
                <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>‚¨Ö Volver al Panel</button>
                
                <h2 style={{ color: '#1e293b' }}>Estad√≠sticas de Precisi√≥n</h2>

                {loading ? <p>Calculando...</p> : (
                    <>
                        {/* 1. KPIs + Gr√°fica Circular */}
                        <div style={{ display: 'flex', gap: 20, marginBottom: 20 }}>
                            <div style={kpiCardStyle}>
                                <h4 style={{margin:0, color:'#64748b'}}>Error Promedio (Kg)</h4>
                                <div style={{height: 100, display: 'flex', flexDirection: 'column', justifyContent: 'center'}}>
                                    <span style={{fontSize: 32, fontWeight:'bold', color: stats.mean > 0 ? '#166534' : '#dc2626'}}>
                                        {stats.mean > 0 ? '+' : ''}{stats.mean} Kg
                                    </span>
                                    <div style={{fontSize:11, color:'#94a3b8'}}>Sesgo general.</div>
                                </div>
                            </div>
                            <div style={kpiCardStyle}>
                                <h4 style={{margin:0, color:'#64748b'}}>Volatilidad (œÉ)</h4>
                                <div style={{height: 100, display: 'flex', flexDirection: 'column', justifyContent: 'center'}}>
                                    <span style={{fontSize: 32, fontWeight:'bold', color: '#475569'}}>¬±{stats.stdDev} Kg</span>
                                    <div style={{fontSize:11, color:'#94a3b8'}}>Desviaci√≥n Est√°ndar.</div>
                                </div>
                            </div>
                            <div style={{...kpiCardStyle, padding: 5, display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
                                <h4 style={{margin:'10px 0 0 0', color:'#64748b'}}>Error % Global (MAPE)</h4>
                                <div style={{ height: 120, width: '100%', position: 'relative' }}>
                                    <ResponsiveContainer width="100%" height="100%">
                                        <PieChart>
                                            <Pie
                                                data={[{ name: 'Error', value: Math.min(stats.mape, 100) }, { name: 'Resto', value: Math.max(0, 100 - stats.mape) }]}
                                                cx="50%" cy="50%" innerRadius={35} outerRadius={50} startAngle={90} endAngle={-270}
                                                dataKey="value" stroke="none"
                                            >
                                                <Cell fill={stats.mape < 15 ? '#22c55e' : stats.mape < 30 ? '#f59e0b' : '#ef4444'} />
                                                <Cell fill="#e5e7eb" />
                                            </Pie>
                                            <Tooltip />
                                        </PieChart>
                                    </ResponsiveContainer>
                                    <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', textAlign: 'center' }}>
                                        <span style={{fontSize: 18, fontWeight: 'bold', color: stats.mape < 15 ? '#166534' : '#7f1d1d'}}>
                                            {stats.mape}%
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </div>

                        {/* 2. Gr√°fica de Barras (Error %) */}
                        <div style={cardGraphStyle}>
                            <h3 style={{textAlign:'center', color:'#555', marginBottom:10}}>Porcentaje de Error por Pedido</h3>
                            <div style={{ height: 300 }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <BarChart data={data} layout="vertical">
                                        <CartesianGrid strokeDasharray="3 3" />
                                        <XAxis type="number" domain={['auto', 'auto']} />
                                        <YAxis dataKey="cliente" type="category" width={100} />
                                        <Tooltip />
                                        <Bar dataKey="errorPct" name="% Error" fill="#82ca9d" barSize={20} />
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>
                        </div>

                        {/* 3. TABLA DETALLADA (CON LA NUEVA COLUMNA ERROR %) */}
                        <h3 style={{marginTop: 30, color: '#334155'}}>Detalle de Desviaciones</h3>
                        <table style={tableStyle}>
                            <thead>
                                <tr>
                                    <th style={thStyle}>Cliente</th>
                                    <th style={thStyle}>Fecha (Pred vs Real)</th>
                                    <th style={thStyle}>Cantidad (Pred vs Real)</th>
                                    <th style={thStyle}>Desviaci√≥n D√≠as</th>
                                    <th style={thStyle}>Desviaci√≥n Kilos</th>
                                    {/* üÜï NUEVA COLUMNA */}
                                    <th style={thStyle}>Error %</th> 
                                </tr>
                            </thead>
                            <tbody>
                                {data.map((item) => {
                                    const fechaPred = new Date(item.fecha);
                                    const fechaReal = new Date(item.pedido.createdAt);
                                    const diffTime = fechaReal - fechaPred;
                                    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)); 

                                    // Estilo de la etiqueta de porcentaje (Rojo > 15%, Verde < 15%)
                                    const isHighError = Math.abs(item.errorPct) > 15;
                                    const pctStyle = {
                                        padding: '4px 10px',
                                        borderRadius: '12px',
                                        fontWeight: 'bold',
                                        fontSize: '12px',
                                        // Rojo suave fondo / Rojo fuerte texto VS Verde suave fondo / Verde fuerte texto
                                        background: isHighError ? '#fee2e2' : '#dcfce7',
                                        color: isHighError ? '#991b1b' : '#166534',
                                        display: 'inline-block'
                                    };

                                    return (
                                        <tr key={item.id} style={{ background: '#fff' }}>
                                            <td style={tdStyle}>
                                                <div style={{fontWeight:'bold', fontSize: 15}}>{item.usuario?.username}</div>
                                                <div style={{fontSize:11, color:'#64748b'}}>{item.usuario?.perfil?.restaurant || 'Sin Restaurante'}</div>
                                            </td>
                                            
                                            <td style={tdStyle}>
                                                <div style={{display:'flex', alignItems:'center', marginBottom: 4}}>
                                                    <span style={{marginRight: 5}}>üóìÔ∏è P:</span> {fechaPred.toLocaleDateString()}
                                                </div>
                                                <div style={{display:'flex', alignItems:'center', fontWeight:'bold', color:'#334155'}}>
                                                    <span style={{marginRight: 5}}>üõí R:</span> {fechaReal.toLocaleDateString()}
                                                </div>
                                            </td>

                                            <td style={tdStyle}>
                                                <div style={{display:'flex', alignItems:'center', marginBottom: 4}}>
                                                    <span style={{marginRight: 5}}>‚öñÔ∏è P:</span> {item.kgPred} Kg
                                                </div>
                                                <div style={{display:'flex', alignItems:'center', fontWeight:'bold', color:'#334155'}}>
                                                    <span style={{marginRight: 5}}>üì¶ R:</span> {item.kgReal} Kg
                                                </div>
                                            </td>

                                            <td style={tdStyle}>
                                                <div style={{fontWeight: 'bold', color: getDayColor(diffDays), fontSize: 15}}>
                                                    {diffDays > 0 ? `+${diffDays}` : diffDays} d√≠as
                                                </div>
                                                <div style={{fontSize:11, color:'#666'}}>
                                                    {diffDays < 0 ? '(Se adelant√≥)' : diffDays > 0 ? '(Se atras√≥)' : '(Exacto)'}
                                                </div>
                                            </td>

                                            <td style={tdStyle}>
                                                <div style={{fontWeight: 'bold', color: getKgColor(item.diffKg), fontSize: 15}}>
                                                    {item.diffKg > 0 ? '+' : ''}{item.diffKg.toFixed(1)} Kg
                                                </div>
                                            </td>

                                            {/* üÜï COLUMNA ERROR % (Estilo Etiqueta) */}
                                            <td style={tdStyle}>
                                                <span style={pctStyle}>
                                                    {item.errorPct}%
                                                </span>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    </>
                )}
            </div>
        </div>
    );
}

export default AdminDesviaciones;
