import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';

function AdminSolicitudes() {
    const [solicitudes, setSolicitudes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [processing, setProcessing] = useState(null); // Para mostrar carga en el botÃ³n especÃ­fico
    const navigate = useNavigate();

    useEffect(() => {
        fetchSolicitudes();
    }, []);

    const fetchSolicitudes = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await api.get('/solicitud-pedidos', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            // FILTRO: Solo mostramos las que NO tienen pedido asociado (pendientes)
            const pendientes = response.data.filter(s => !s.pedido);
            
            // Ordenamos por fecha de creaciÃ³n (mÃ¡s antiguas primero para atenderlas)
            setSolicitudes(pendientes.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt)));
        } catch (error) {
            console.error("Error cargando solicitudes", error);
        } finally {
            setLoading(false);
        }
    };

    // âš™ï¸ LÃ“GICA PRINCIPAL: CONVERTIR SOLICITUD -> PEDIDO
    const handleCrearPedido = async (solicitud) => {
        if (!window.confirm(`Â¿Confirmar pedido para ${solicitud.usuario?.username}?`)) return;

        setProcessing(solicitud.id);
        try {
            const token = localStorage.getItem('token');
            
            // Preparamos el DTO que espera tu backend (createPedidoDto)
            const payload = {
                usuarioId: solicitud.usuario.id,
                solicitudId: solicitud.id,
                // Usamos la fecha sugerida por el cliente o la actual si no hay
                fechaEntrega: solicitud.fechaEntrega || new Date().toISOString(), 
                entregado: false,
                pagado: false,
                notas: solicitud.notas
            };

            await api.post('/pedidos', payload, {
                headers: { Authorization: `Bearer ${token}` }
            });

            alert('âœ… Pedido generado correctamente');
            // Recargamos la lista (la solicitud desaparecerÃ¡ porque ya tendrÃ¡ pedido)
            fetchSolicitudes();

        } catch (error) {
            console.error("Error creando pedido", error);
            alert("Error al generar el pedido. Revisa la consola.");
        } finally {
            setProcessing(null);
        }
    };

    // Estilos
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden' };
    const thStyle = { background: '#475569', color: '#fff', padding: 15, textAlign: 'left' };
    const tdStyle = { padding: 15, borderBottom: '1px solid #e2e8f0', color: '#333' };
    const btnStyle = { padding: '8px 15px', background: '#8b5cf6', color: '#fff', border: 'none', borderRadius: 5, cursor: 'pointer', fontWeight: 'bold' };

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20 }}>
            <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>â¬… Volver</button>
            
            <h2 style={{ color: '#1e293b' }}>Solicitudes Pendientes de AprobaciÃ³n</h2>
            <p style={{ color: '#64748b' }}>Estas solicitudes requieren tu confirmaciÃ³n para pasar a producciÃ³n.</p>

            {loading ? <p>Cargando...</p> : (
                solicitudes.length === 0 ? (
                    <div style={{ marginTop: 30, textAlign: 'center', color: '#64748b', fontSize: 18 }}>
                        ðŸŽ‰ Â¡Todo al dÃ­a! No hay solicitudes pendientes.
                    </div>
                ) : (
                    <table style={tableStyle}>
                        <thead>
                            <tr>
                                <th style={thStyle}>Fecha Solicitud</th>
                                <th style={thStyle}>Cliente</th>
                                <th style={thStyle}>Requerimiento (Kg)</th>
                                <th style={thStyle}>Entrega Sugerida</th>
                                <th style={thStyle}>AcciÃ³n</th>
                            </tr>
                        </thead>
                        <tbody>
                            {solicitudes.map(s => (
                                <tr key={s.id}>
                                    <td style={tdStyle}>
                                        {new Date(s.createdAt).toLocaleDateString()}
                                        <div style={{fontSize: 12, color: '#94a3b8'}}>Hace {Math.floor((new Date() - new Date(s.createdAt)) / (1000 * 60 * 60 * 24))} dÃ­as</div>
                                    </td>
                                    <td style={tdStyle}>
                                        <strong>{s.usuario?.username || 'Desconocido'}</strong>
                                        <div style={{fontSize: 12, color: '#64748b'}}>{s.usuario?.email}</div>
                                    </td>
                                    <td style={tdStyle}>
                                        <div>â˜• Grano: {s.grano}</div>
                                        <div>ðŸ§ª Molido: {s.molido}</div>
                                        {s.notas && <div style={{fontSize: 12, color: '#d97706', marginTop: 5}}>Nota: {s.notas}</div>}
                                    </td>
                                    <td style={tdStyle}>
                                        {s.fechaEntrega ? new Date(s.fechaEntrega).toLocaleDateString() : 'No especificada'}
                                    </td>
                                    <td style={tdStyle}>
                                        <button 
                                            style={{...btnStyle, opacity: processing === s.id ? 0.7 : 1}}
                                            onClick={() => handleCrearPedido(s)}
                                            disabled={processing === s.id}
                                        >
                                            {processing === s.id ? 'Procesando...' : 'âœ… Crear Pedido'}
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )
            )}
        </div>
    );
}

export default AdminSolicitudes;
