import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';

function AdminDesviaciones() {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await api.get('/predicciones/analisis-desviacion', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setData(response.data);
        } catch (error) {
            console.error("Error cargando an√°lisis", error);
        } finally {
            setLoading(false);
        }
    };

    // Estilos
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden', fontSize: 14 };
    const thStyle = { background: '#334155', color: '#fff', padding: '12px 15px', textAlign: 'left' };
    const tdStyle = { padding: '12px 15px', borderBottom: '1px solid #e2e8f0', color: '#333', verticalAlign: 'middle' };

    // Helper para colorear desviaciones
    const getColor = (val, isDay = false) => {
        if (val === 0) return '#166534'; // Exacto (Verde oscuro)
        // Para d√≠as: negativo es "pidi√≥ antes" (bueno/alerta), positivo es "pidi√≥ tarde" (riesgo)
        // Para kilos: negativo es "pidi√≥ menos" (malo), positivo es "pidi√≥ m√°s" (bueno)
        return Math.abs(val) > (isDay ? 5 : 2) ? '#dc2626' : '#d97706'; // Rojo si es mucha diferencia, Naranja si es poca
    };

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20 }}>
            <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>‚¨Ö Volver al Panel</button>
            
            <h2 style={{ color: '#1e293b' }}>Precisi√≥n de Predicciones</h2>
            <p style={{ color: '#64748b' }}>Comparativa entre lo estimado y el comportamiento real del cliente.</p>

            {loading ? <p>Calculando...</p> : (
                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th style={thStyle}>Cliente</th>
                            <th style={thStyle}>Fecha (Pred vs Real)</th>
                            <th style={thStyle}>Cantidad (Pred vs Real)</th>
                            <th style={thStyle}>Desviaci√≥n D√≠as</th>
                            <th style={thStyle}>Desviaci√≥n Kilos</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((item) => {
                            // 1. C√ÅLCULO DE FECHAS
                            const fechaPred = new Date(item.fecha);
                            const fechaReal = new Date(item.pedido.createdAt); // Fecha en que se cre√≥ el pedido
                            
                            // Diferencia en d√≠as (Real - Predicho)
                            // Negativo = Pidi√≥ ANTES de lo esperado
                            // Positivo = Pidi√≥ DESPU√âS de lo esperado
                            const diffTime = fechaReal - fechaPred;
                            const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)); 

                            // 2. C√ÅLCULO DE KILOS
                            const kgPred = item.cantidad;
                            const kgReal = (item.pedido.solicitudPedido?.grano || 0) + (item.pedido.solicitudPedido?.molido || 0);
                            const diffKg = (kgReal - kgPred).toFixed(1);

                            return (
                                <tr key={item.id} style={{ background: '#fff' }}>
                                    <td style={tdStyle}>
                                        <div style={{fontWeight:'bold'}}>{item.usuario?.username}</div>
                                        <div style={{fontSize:11, color:'#64748b'}}>{item.usuario?.perfil?.restaurant || 'Sin Restaurante'}</div>
                                    </td>
                                    
                                    {/* FECHAS */}
                                    <td style={tdStyle}>
                                        <div style={{fontSize:12}}>üìÖ P: {fechaPred.toLocaleDateString()}</div>
                                        <div style={{fontSize:12, fontWeight:'bold'}}>üõí R: {fechaReal.toLocaleDateString()}</div>
                                    </td>

                                    {/* CANTIDADES */}
                                    <td style={tdStyle}>
                                        <div style={{fontSize:12}}>‚öñÔ∏è P: {kgPred} Kg</div>
                                        <div style={{fontSize:12, fontWeight:'bold'}}>üì¶ R: {kgReal} Kg</div>
                                    </td>

                                    {/* DESVIACI√ìN D√çAS */}
                                    <td style={tdStyle}>
                                        <span style={{
                                            fontWeight: 'bold',
                                            color: getColor(diffDays, true)
                                        }}>
                                            {diffDays > 0 ? `+${diffDays}` : diffDays} d√≠as
                                        </span>
                                        <div style={{fontSize:10, color:'#666'}}>
                                            {diffDays < 0 ? '(Se adelant√≥)' : diffDays > 0 ? '(Se atras√≥)' : '(Exacto)'}
                                        </div>
                                    </td>

                                    {/* DESVIACI√ìN KILOS */}
                                    <td style={tdStyle}>
                                        <span style={{
                                            fontWeight: 'bold',
                                            color: diffKg < 0 ? '#dc2626' : '#166534' // Rojo si vendimos menos, Verde si vendimos m√°s o igual
                                        }}>
                                            {diffKg > 0 ? `+${diffKg}` : diffKg} Kg
                                        </span>
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            )}
            
            {!loading && data.length === 0 && (
                <p style={{textAlign:'center', marginTop:20, color:'#666'}}>No hay predicciones vinculadas a pedidos todav√≠a.</p>
            )}
        </div>
    );
}

export default AdminDesviaciones;
