import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Login from './pages/Login'; // Ajusta la ruta según dónde lo guardaste
import Bienvenida from './pages/Bienvenida';
import Contacto from './pages/Contacto';

// ¡Paso 1: Importa el archivo CSS Module!
import styles from './Home.module.css';

function Home() {
  return (
    // ¡Paso 2: Usa className en lugar de style!
    <div className={styles.container}> 
      
      {/* Mejora: Usamos <nav> para la barra de navegación, 
        es mejor para la accesibilidad (HTML semántico).
      */}
      <nav className={styles.topBar}>
        
        {/* Mejora: Aplicamos el estilo directo al <Link>
          en lugar de poner un <button> adentro. Es más limpio.
        */}
        <Link to="/contacto" className={styles.navLink}>
          Contáctanos
        </Link>
        
        <div className={styles.logo}>
          Logo
        </div>
        
        <Link to="/login" className={styles.navLink}>
          Iniciar sesión
        </Link>
      </nav>

      {/* Área central tipo "hero" */}
      <div className={styles.heroSection}>
        <h2>Información de la empresa</h2>
        <p>Aquí puedes poner texto corto, descripción y tus servicios principales.</p>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path='/Bienvenida' element={<Bienvenida />}/>
        <Route path='/Contacto' element={<Contacto />}/>
      </Routes>
    </Router>
  );
}

export default App;
