import React, { useEffect } from 'react'; 
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import Login from './pages/Login'; 
import Bienvenida from './pages/Bienvenida';
import Contacto from './pages/Contacto';
// Importamos el logo (se mantiene)
import logoCafe from './assets/logo-cafe-tech.png'; 

// Importa los módulos de Font Awesome (asumiendo que están instalados)
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram } from '@fortawesome/free-brands-svg-icons';

// Importa las páginas restantes
import SolicitudPedidoForm from './pages/SolicitudPedidoForm';
import PedidosAnteriores from './pages/PedidosAnteriores';
import DetallePedido from './pages/DetallePedido';
import Pagos from './pages/Pagos';
import Predicciones from './pages/Predicciones';
import Inventario from './pages/Inventario';
import AnalisisNegocio from './pages/AnalisisNegocio';
import AdminPedidos from './pages/admin/AdminPedidos';
import AdminDashboard from './pages/admin/AdminDashboard';
import AdminSolicitudes from './pages/admin/AdminSolicitudes';
import AdminClientes from './pages/admin/AdminClientes';
import AdminPagos from './pages/admin/AdminPagos';
import AdminSaldos from './pages/admin/AdminSaldos';
import AdminPredicciones from './pages/admin/AdminPredicciones';
import AdminDesviaciones from './pages/admin/AdminDesviaciones';
import AdminInventario from './pages/admin/AdminInventario';
import AdminPlaneador from './pages/admin/AdminPlaneador';
import AdminBroadcast from './pages/admin/AdminBroadcast';


// -------------------------------------------------------------
// COMPONENTE HOME (ESTILOS INLINE Y ESTRUCTURA FINAL)
// -------------------------------------------------------------
function Home() {
    
    // --- ESTILOS DEFINIDOS COMO OBJETOS JAVASCRIPT ---
    const PAGE_CONTAINER_STYLE = {
        padding: '20px',
        fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
        minHeight: '100vh',
        backgroundColor: '#FFFFFF', // FONDO BLANCO
        color: '#1e293b',
        display: 'flex',
        flexDirection: 'column',
    };

    const NAV_BAR_STYLE = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '15px 40px',
        borderBottom: '1px solid #e2e8f0',
        marginBottom: '60px',
        color: '#1e293b',
        fontSize: '1.8rem',
        fontWeight: 'bold',
        letterSpacing: '0.5px',
    };

    const NAV_LINKS_RIGHT_STYLE = {
        display: 'flex',
        gap: '25px',
    };

    const NAV_LINK_BASE_STYLE = {
        color: '#3b82f6',
        textDecoration: 'none',
        fontWeight: '500',
        fontSize: '1rem',
        padding: '5px 0',
        transition: 'color 0.3s ease',
    };

    const NAV_LINK_LOGIN_STYLE = {
        ...NAV_LINK_BASE_STYLE,
        color: '#78549b',
        fontWeight: 'bold',
    };

    const CARD_GRID_STYLE = {
        // CRÍTICO: FORZAR LAYOUT FLEXBOX para garantizar el alineamiento paralelo
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
        alignItems: 'flex-start', // Asegura que todas comiencen en la misma línea superior
        gap: '20px',
        maxWidth: '1200px',
        margin: '40px auto',
        flexGrow: '1',
    };
    
    const CARD_BASE_STYLE = {
        // CRÍTICO: Cálculo de ancho explícito para 3 columnas con Flexbox
        flex: '0 0 calc(33.333% - 14px)',
        minWidth: '300px',
        backgroundColor: '#ffffff',
        border: '1px solid #e2e8f0',
        borderRadius: '12px',
        overflow: 'hidden',
        boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
        display: 'flex',
        flexDirection: 'column',
        height: 'auto', // Dejar que el contenido defina la altura
    };

    const CARD_IMAGE_STYLE = {
        width: '100%',
        height: '200px',
        objectFit: 'cover',
        borderRadius: '12px 12px 0 0',
        marginBottom: '15px',
    };

    const CARD_CONTENT_STYLE = {
        padding: '0 25px 25px 25px',
        flexGrow: '1',
    };

    const CARD_TITLE_STYLE = {
        color: '#78549b',
        fontSize: '1.6rem',
        marginTop: '0',
        marginBottom: '15px',
        borderBottom: '1px solid #e2e8f0',
        paddingBottom: '10px',
    };

    const CARD_PARAGRAPH_STYLE = {
        color: '#475569',
        fontSize: '0.95rem',
        lineHeight: '1.6',
        marginBottom: '0',
    };

    const FOOTER_STYLE = {
        textAlign: 'center',
        padding: '30px',
        marginTop: '50px',
        borderTop: '1px solid #e2e8f0',
        color: '#475569',
    };

    const INSTAGRAM_ICON_STYLE = {
        fontSize: '2.5rem',
        color: '#78549b',
        transition: 'color 0.3s ease',
    };

    return (
        <div style={PAGE_CONTAINER_STYLE}> 
            
            {/* Barra Superior */}
            <nav style={NAV_BAR_STYLE}>
                <span>El puente entre la tierra y la taza</span>
                <div style={NAV_LINKS_RIGHT_STYLE}>
                    <Link to="/contacto" style={NAV_LINK_BASE_STYLE}>Contact Us</Link>
                    <Link to="/login" style={NAV_LINK_LOGIN_STYLE}>Login</Link>
                </div>
            </nav>

            {/* Contenedor de la Cuadrícula de Tarjetas */}
            <div style={CARD_GRID_STYLE}>

                {/* 1. Tarjeta: Nosotros (Alianza y Calidad) */}
                <div style={CARD_BASE_STYLE}>
                    <img 
                        src="https://via.placeholder.com/600x400/2C3E50/FFFFFF?text=Plantacion+Cafe" 
                        alt="Plantación de café" 
                        style={CARD_IMAGE_STYLE} 
                    />
                    <div style={CARD_CONTENT_STYLE}>
                        <h3 style={CARD_TITLE_STYLE}>Nosotros</h3>
                        <p style={CARD_PARAGRAPH_STYLE}>
                            Somos la alianza que eleva la <strong>calidad premium</strong> de nuestro café de denominación poblana. Nuestra plataforma te asegura <strong>Robustez</strong>, <strong>Disponibilidad</strong> y la <strong>Transparencia</strong> total en cada paso de tu operación.
                        </p>
                    </div>
                </div>
                
                {/* 2. Tarjeta: Nuestro Modelo (Logística y Flexibilidad) */}
                <div style={CARD_BASE_STYLE}>
                    <img 
                        src="https://via.placeholder.com/600x400/2C3E50/FFFFFF?text=Granos+de+Cafe" 
                        alt="Granos de café" 
                        style={CARD_IMAGE_STYLE} 
                    />
                    <div style={CARD_CONTENT_STYLE}>
                        <h3 style={CARD_TITLE_STYLE}>Nuestro Modelo</h3>
                        <p style={CARD_PARAGRAPH_STYLE}>
                            Tu cadena de suministro necesita <strong>Velocidad</strong> y <strong>Flexibilidad</strong>. Garantizamos la entrega de tu café en <strong>grano o molido</strong> en un periodo típico de solo **4 a 36 horas** después de tu pedido, sin importar la hora del día.
                        </p>
                    </div>
                </div>

                {/* 3. Tarjeta: Enfoque Tecnológico (Digitalización y Sin Mínimos) */}
                <div style={CARD_BASE_STYLE}>
                    <img 
                        src="https://via.placeholder.com/600x400/2C3E50/FFFFFF?text=Cafe+Digital" 
                        alt="Taza de café digital" 
                        style={CARD_IMAGE_STYLE} 
                    />
                    <div style={CARD_CONTENT_STYLE}>
                        <h3 style={CARD_TITLE_STYLE}>Enfoque Tecnológico</h3>
                        <p style={CARD_PARAGRAPH_STYLE}>
                            Esta eficiencia es impulsada por nuestra <strong>Digitalización</strong> total que te da la libertad de <strong>no pedir consumos mínimos mensuales</strong>. Es la <strong>Eficiencia</strong> que mereces.
                        </p>
                    </div>
                </div>

            </div>

            {/* Footer con Ubicaciones e Instagram */}
            <footer style={FOOTER_STYLE}>
                <p>Santiago de Querétaro, Qro</p>
                <p>Xicotepec de Juárez, Pue</p>
                <a href="https://www.instagram.com/" target="_blank" rel="noopener noreferrer">
                    <FontAwesomeIcon icon={faInstagram} style={INSTAGRAM_ICON_STYLE} />
                </a>
            </footer>
        </div>
    );
}

// -------------------------------------------------------------
// COMPONENTE APP (Se mantiene igual)
// -------------------------------------------------------------
function AuthRedirect({ children }) {
    const navigate = useNavigate();
    const token = localStorage.getItem('token');
    
    useEffect(() => {
        if (token && (window.location.pathname === '/' || window.location.pathname === '/login')) {
            navigate('/Bienvenida', { replace: true });
        }
    }, [token, navigate]);

    return children;
}


function App() {
    return (
        <Router>
            <AuthRedirect>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/login" element={<Login />} />
                    <Route path='/Bienvenida' element={<Bienvenida />}/>
                    <Route path='/Contacto' element={<Contacto />}/>
                    <Route path='/solicitud-pedido' element={<SolicitudPedidoForm />}/>
                    <Route path='/pedidos-anteriores' element={<PedidosAnteriores />}/>
                    <Route path='/predicciones' element={<Predicciones />}/>
                    <Route path='/pedidos/:id' element={<DetallePedido />}/>
                    <Route path='/inventario' element={<Inventario />}/>
                    <Route path='/pagos' element={<Pagos />}/>
                    <Route path='/analisis-negocio' element={<AnalisisNegocio />}/>
                    {/* Rutas de Admin (sin modificar contenido) */}
                    <Route path='/admin' element={<AdminDashboard />} />
                    <Route path='/admin/pedidos' element={<AdminPedidos />} />
                    <Route path='/admin/solicitudes' element='/admin/solicitudes' />
                    <Route path='/admin/clientes' element={<AdminClientes />} />
                    <Route path='/admin/pagos' element={<AdminPagos />} />
                    <Route path='/admin/saldos' element={<AdminSaldos />} />
                    <Route path='/admin/predicciones' element={<AdminPredicciones />} />
                    <Route path='/admin/desviaciones' element={<AdminDesviaciones />} />
                    <Route path='/admin/inventario' element={<AdminInventario />} />
                    <Route path='/admin/planeador' element={<AdminPlaneador />} />
                    <Route path='/admin/broadcast' element={<AdminBroadcast />} />
                </Routes>
            </AuthRedirect>
        </Router>
    );
}

export default App;
