import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';

function AdminPedidos() {
    const [pedidos, setPedidos] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchPedidos();
    }, []);

    const fetchPedidos = async () => {
        try {
            const token = localStorage.getItem('token');
            // Llamamos al endpoint GET /pedidos (que trae TODOS)
            const response = await api.get('/pedidos', {
                headers: { Authorization: `Bearer ${token}` }
            });
            // Ordenamos: más recientes primero
            const sorted = response.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
            setPedidos(sorted);
        } catch (error) {
            console.error("Error fetching pedidos", error);
        } finally {
            setLoading(false);
        }
    };

    // Función para cambiar estado (Pagado / Entregado)
    const toggleStatus = async (id, field, currentValue) => {
        try {
            const token = localStorage.getItem('token');
            const payload = { [field]: !currentValue }; // Invertir valor
            
            await api.patch(`/pedidos/${id}`, payload, {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            // Recargar la lista para ver el cambio
            fetchPedidos();
        } catch (error) {
            console.error("Error updating pedido", error);
            alert("Error al actualizar el pedido");
        }
    };

    // Estilos de tabla
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden' };
    const thStyle = { background: '#334155', color: '#fff', padding: 15, textAlign: 'left' };
    const tdStyle = { padding: 15, borderBottom: '1px solid #e2e8f0', color: '#333' };
    const btnActionStyle = (active) => ({
        padding: '5px 10px', borderRadius: 5, border: 'none', cursor: 'pointer', fontWeight: 'bold',
        background: active ? '#bbf7d0' : '#fecaca',
        color: active ? '#166534' : '#991b1b',
        marginRight: 5
    });

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20 }}>
            <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>⬅ Volver al Panel</button>
            
            <h2 style={{ color: '#1e293b' }}>Gestión de Pedidos Globales</h2>

            {loading ? <p>Cargando...</p> : (
                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th style={thStyle}>ID / Fecha</th>
                            <th style={thStyle}>Cliente</th>
                            <th style={thStyle}>Detalle</th>
                            <th style={thStyle}>Estado Entrega</th>
                            <th style={thStyle}>Estado Pago</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pedidos.map(p => (
                            <tr key={p.id}>
                                <td style={tdStyle}>
                                    <div style={{fontWeight: 'bold'}}>{new Date(p.createdAt).toLocaleDateString()}</div>
                                    <div style={{fontSize: 12, color: '#666'}}>{p.id.split('-')[0]}...</div>
                                </td>
                                <td style={tdStyle}>
                                    {/* Asumiendo que el backend devuelve la relación usuario */}
                                    {p.usuario?.username || 'Cliente'}
                                    <div style={{fontSize:12, color:'#666'}}>{p.usuario?.email}</div>
                                </td>
                                <td style={tdStyle}>
                                    {p.solicitudPedido ? (
                                        <>
                                            <div>Grano: {p.solicitudPedido.grano}kg</div>
                                            <div>Molido: {p.solicitudPedido.molido}kg</div>
                                        </>
                                    ) : 'Sin detalle'}
                                </td>
                                <td style={tdStyle}>
                                    <button 
                                        style={btnActionStyle(p.entregado)}
                                        onClick={() => toggleStatus(p.id, 'entregado', p.entregado)}
                                    >
                                        {p.entregado ? 'ENTREGADO' : 'PENDIENTE'}
                                    </button>
                                </td>
                                <td style={tdStyle}>
                                    <button 
                                        style={btnActionStyle(p.pagado)}
                                        onClick={() => toggleStatus(p.id, 'pagado', p.pagado)}
                                    >
                                        {p.pagado ? 'PAGADO' : 'PENDIENTE'}
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default AdminPedidos;
