import React, { useState } from 'react';
import api from '../api/api'; 
import { useNavigate, Link } from 'react-router-dom';

function Login({ onLoginSuccess }) {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await api.post('/auth/login', { email, password });
            
            const { accessToken, user } = response.data;

            if (!accessToken) {
                setError('No se recibi칩 token v치lido');
                return;
            }

            localStorage.setItem('token', accessToken);
            if (onLoginSuccess) onLoginSuccess(accessToken);

            if (user && user.rol === 'Admin') {
                navigate('/admin');
            } else {
                navigate('/bienvenida');
            }

        } catch (err) {
            console.error(err);
            setError('Email o contrase침a incorrectos');
        }
    };

    // --- Estilos Adaptados al Tema Oscuro ---
    const PAGE_CONTAINER = { 
        minHeight: '100vh', 
        background: '#1e293b', // Fondo oscuro
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'center' 
    };
    
    const navBarButtonStyle = { 
        background: '#7dd3fc', 
        color: '#1e293b', 
        padding: '10px 30px', 
        borderRadius: 8, 
        border: 'none',
        fontWeight: 'bold',
        cursor: 'pointer'
    };
    
    const formCardStyle = { 
        width: 380, 
        background: '#334155', // Fondo de tarjeta oscuro
        borderRadius: 16, 
        border: '2px solid #475569', 
        padding: 32, 
        boxShadow: '0 8px 20px rgba(0,0,0,0.4)', 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center' 
    };
    
    const welcomeBannerStyle = { 
        background: '#a78Bfa', // Lila
        color: '#1e293b',
        borderRadius: 8, 
        marginBottom: 18, 
        padding: '10px 0', 
        width: '100%', 
        textAlign: 'center', 
        fontWeight: 'bold'
    };
    
    const inputStyle = { 
        width: '100%', 
        padding: 12, 
        marginBottom: 18, 
        borderRadius: 8, 
        background: '#1e293b', // Input m치s oscuro
        fontSize: 16, 
        border: '1px solid #475569',
        color: '#fff' // Texto blanco
    };
    
    const submitButtonStyle = { 
        width: '100%', 
        background: '#10b981', // Verde
        color: '#1e293b', 
        padding: 14, 
        borderRadius: 8, 
        fontWeight: 'bold', 
        fontSize: 16, 
        border: 'none', 
        marginBottom: 12,
        cursor: 'pointer',
        transition: 'background 0.2s'
    };
    
    const secondaryButtonStyle = { 
        width: '100%', 
        background: '#475569', // Gris intermedio
        color: '#cbd5e1', // Texto gris claro
        padding: 10, 
        borderRadius: 8, 
        fontSize: 14, 
        border: 'none',
        cursor: 'pointer',
        transition: 'background 0.2s'
    };

    return (
        <div style={PAGE_CONTAINER}>
            {/* Barra superior (Adaptada al tema oscuro) */}
            <div style={{ width: 500, display: 'flex', justifyContent: 'space-between', marginBottom: 40 }}>
                <Link to="/contacto" style={navBarButtonStyle}>Contactanos</Link>
                <button style={{...navBarButtonStyle, background: '#a78Bfa', color: '#1e293b'}}>Login</button>
                <Link to="/" style={navBarButtonStyle}>Inicio</Link>
            </div>
            
            {/* Card central */}
            <div style={formCardStyle}>
                <div style={welcomeBannerStyle}>游녦 Hola de regreso</div>
                <form style={{ width: '100%' }} onSubmit={handleSubmit}>
                    <label style={{ marginBottom: 4, display: 'block', fontWeight: 'bold', color: '#94a3b8' }}>Email</label>
                    <input type="email" value={email} onChange={e => setEmail(e.target.value)} style={inputStyle} required />
                    
                    <label style={{ marginBottom: 4, display: 'block', fontWeight: 'bold', color: '#94a3b8' }}>Contrase침a</label>
                    <input type="password" value={password} onChange={e => setPassword(e.target.value)} style={inputStyle} required />
                    
                    <button type="submit" style={submitButtonStyle}>Ingresar</button>
                    
                    {error && <div style={{ color: '#ef4444', textAlign: 'center', marginBottom: 8, fontSize: 14 }}>{error}</div>}
                </form>
                <button style={secondaryButtonStyle}>Olvid칠 mi contrase침a</button>
            </div>
        </div>
    );
}

export default Login;
