import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../api/api'; // ‚¨ÖÔ∏è Importamos la API

function AdminDashboard() {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login', { replace: true });
    };

    // üîí VALIDACI√ìN DE SESI√ìN ACTIVA
    useEffect(() => {
        const verifySession = async () => {
            const token = localStorage.getItem('token');
            
            // 1. Si no hay token f√≠sico, adi√≥s.
            if (!token) {
                handleLogout();
                return;
            }

            try {
                // 2. Hacemos una petici√≥n de prueba (Profile) para ver si el token sigue vivo
                await api.get('/profile/me', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                // Si responde bien (200), no hacemos nada, el usuario sigue en el dashboard.

            } catch (error) {
                // 3. Si el servidor dice 401 (No autorizado), el token venci√≥.
                if (error.response && error.response.status === 401) {
                    console.log("Sesi√≥n expirada. Redirigiendo al login...");
                    handleLogout();
                }
            }
        };

        verifySession();
    }, [navigate]); // Se ejecuta al montar el componente

    // Estilos
    const containerStyle = { minHeight: '100vh', background: '#1e293b', color: '#fff', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' };
    const cardStyle = { background: '#334155', padding: 30, borderRadius: 12, width: 300, textAlign: 'center', margin: 15, cursor: 'pointer', border: '1px solid #475569', transition: '0.3s' };
    const titleStyle = { fontSize: 24, fontWeight: 'bold', color: '#a78Bfa', marginBottom: 10 };

    // Helper para efectos hover
    const handleMouseEnter = (e) => e.currentTarget.style.background = '#475569';
    const handleMouseLeave = (e) => e.currentTarget.style.background = '#334155';

    return (
        <div style={containerStyle}>
            <div style={{ width: '100%', maxWidth: 800, display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 40 }}>
                <h1 style={{ color: '#a78Bfa' }}>Panel de Administrador</h1>
                <button onClick={handleLogout} style={{ background: '#ef4444', color: '#fff', padding: '10px 20px', borderRadius: 8, border: 'none', cursor: 'pointer' }}>Cerrar Sesi√≥n</button>
            </div>

            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                
                {/* 1. SOLICITUDES */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/solicitudes')}
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>üì©</div>
                    <div style={titleStyle}>Solicitudes</div>
                    <p>Revisar y aprobar nuevas solicitudes de clientes.</p>
                </div>

                {/* 2. PEDIDOS ACTIVOS */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/pedidos')}
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>üì¶</div>
                    <div style={titleStyle}>Pedidos Activos</div>
                    <p>Controlar entregas y pagos de pedidos ya creados.</p>
                </div>

                {/* 3. Clientes */}
                <div 
                    style={cardStyle}
                    onClick={() => navigate('/admin/clientes')} 
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>üë•</div>
                    <div style={titleStyle}>Clientes</div>
                    <p>Ver lista de usuarios, restaurantes y precios acordados.</p>
                </div>

                {/* 4. Pagos */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/pagos')} 
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>üí∞</div>
                    <div style={titleStyle}>Pagos Globales</div>
                    <p>Historial de ingresos y registro manual de cobros.</p>
                </div>

                {/* 5. SALDOS */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/saldos')} 
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>‚öñÔ∏è</div>
                    <div style={titleStyle}>Saldos Clientes</div>
                    <p>Ver qui√©n debe dinero y el estado de cuenta global.</p>
                </div>

                {/* 6. AN√ÅLISIS DE PREDICCIONES */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/predicciones')} 
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>üìà</div>
                    <div style={titleStyle}>Predicciones</div>
                    <p>Analizar estimaciones y vincular con ventas reales.</p>
                </div>

                {/* 7. REPORTE DE DESVIACI√ìN */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/desviaciones')} 
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>üéØ</div>
                    <div style={titleStyle}>Precisi√≥n</div>
                    <p>Comparativa: Predicci√≥n vs Realidad (D√≠as y Kg).</p>
                </div>

                {/* 7. INVENTARIO GLOBAL (NUEVO) */}
                <div 
                    style={cardStyle} 
                    onClick={() => navigate('/admin/inventario')} 
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div style={{ fontSize: 40, marginBottom: 10 }}>üóÉÔ∏è</div>
                    <div style={titleStyle}>Inventario</div>
                    <p>Monitorear niveles de stock de todos los clientes.</p>
                </div>
            </div>
        </div>
    );
}

export default AdminDashboard;
