import React, { useState, useEffect } from 'react'; // ⬅️ Correcto
import api from '../api/api';
import ClientLayout from '../components/ClientLayout';
import { useNavigate } from 'react-router-dom'; // ⬅️ Re-agregado: necesario para navegación interna y manejo de errores

// ⬇️ IMPORTANTE: Agregar todos los componentes de Recharts si los usas en el renderizado
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend, LineChart, Line, CartesianGrid } from 'recharts';

function AnalisisNegocio() {
    const [analysisData, setAnalysisData] = useState(null);
    const [loading, setLoading] = useState(true);
    // Aunque ClientLayout maneja el auth, usamos useNavigate por si necesitamos ir a otra ruta
    const navigate = useNavigate(); 

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) return;

        const config = { headers: { Authorization: `Bearer ${token}` } };

        api.get('/pedidos/analytics/me', config)
            .then(res => {
                setAnalysisData(res.data);
                setLoading(false);
            })
            .catch(err => {
                console.error('Error fetching analysis data', err);
                setLoading(false);
            });
    }, []);

    const contentStyle = {
        width: '100%', 
        maxWidth: 800, 
        padding: 25,
        background: '#334155', 
        borderRadius: 12, 
        boxShadow: '0 4px 10px rgba(0,0,0,0.3)',
        color: '#fff'
    };
    
    const innerCardStyle = { 
        padding: 15, 
        background: '#1e293b', 
        borderRadius: 8, 
        borderLeft: '4px solid #a78Bfa' 
    };

    return (
        <ClientLayout title="Análisis de Negocio">
            <div style={contentStyle}>
                <h3 style={{ color: '#7dd3fc', borderBottom: '1px solid #475569', paddingBottom: 10, margin: '0 0 20px 0' }}>Tu Desempeño y Consumo</h3>
                
                {loading && <p>Cargando datos de análisis...</p>}
                
                {!loading && analysisData && (
                    // ... (El contenido de las tarjetas y gráficos va aquí) ...
                    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: 20 }}>
                        <div style={{...innerCardStyle, borderLeftColor: '#f59e0b'}}>
                            <p style={{ color: '#94a3b8', margin: 0 }}>Total de Pedidos</p>
                            <h4 style={{ color: '#f59e0b', fontSize: 24, margin: '5px 0 0 0' }}>{analysisData.resumenTotal.totalPedidos}</h4>
                        </div>
                        
                        <div style={{...innerCardStyle, borderLeftColor: '#10b981'}}>
                            <p style={{ color: '#94a3b8', margin: 0 }}>Kg Consumidos (últ. 12m)</p>
                            <h4 style={{ color: '#10b981', fontSize: 24, margin: '5px 0 0 0' }}>{analysisData.resumenTotal.kgConsumidos} Kg</h4>
                        </div>
                        
                        {/* El resto de las tarjetas y gráficos de AnalisisNegocio.jsx original deben ser re-insertados aquí */}
                        
                        <div style={{ gridColumn: '1 / -1', marginTop: 20, borderTop: '1px solid #475569', paddingTop: 20 }}>
                            <h4 style={{ color: '#cbd5e1' }}>Gráfico de Consumo Mensual (simulado)</h4>
                            <div style={{ height: 250, background: '#1e293b', borderRadius: 8, display: 'flex', justifyContent: 'center', alignItems: 'center', color: '#64748b', fontSize: 14 }}>
                                [Gráfico de Barras o Líneas de Recharts]
                            </div>
                        </div>
                    </div>
                )}

                {!loading && !analysisData && <p>No se encontraron datos de análisis.</p>}

            </div>
        </ClientLayout>
    );
}

export default AnalisisNegocio;
