import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';
// Asegúrate de tener instalada la librería recharts: npm install recharts
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';

function AdminDesviaciones() {
    const [data, setData] = useState([]);
    const [stats, setStats] = useState({ stdDev: 0, mean: 0, mape: 0 }); 
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await api.get('/predicciones/analisis-desviacion', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            const rawData = response.data;
            
            // 1. PROCESAR DATOS
            const processedData = rawData.map(item => {
                const kgPred = item.cantidad;
                const kgReal = (item.pedido.solicitudPedido?.grano || 0) + (item.pedido.solicitudPedido?.molido || 0);
                const diffKg = kgReal - kgPred;
                const errorPct = kgPred !== 0 ? ((diffKg / kgPred) * 100).toFixed(1) : 0;

                return {
                    ...item,
                    cliente: item.usuario?.username || 'Anon',
                    kgPred,
                    kgReal,
                    diffKg,
                    errorPct: parseFloat(errorPct)
                };
            });

            // 2. CALCULAR ESTADÍSTICAS
            if (processedData.length > 0) {
                const diffs = processedData.map(d => d.diffKg);
                const mean = diffs.reduce((a, b) => a + b, 0) / diffs.length;
                const variance = diffs.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / diffs.length;
                const stdDev = Math.sqrt(variance);
                
                const absPctErrors = processedData.map(d => Math.abs(d.errorPct));
                const mape = absPctErrors.reduce((a, b) => a + b, 0) / absPctErrors.length;

                setStats({ 
                    mean: parseFloat(mean.toFixed(2)), 
                    stdDev: parseFloat(stdDev.toFixed(2)),
                    mape: parseFloat(mape.toFixed(1))
                });
            }

            setData(processedData);

        } catch (error) {
            console.error("Error cargando análisis", error);
        } finally {
            setLoading(false);
        }
    };

    // Estilos
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden', fontSize: 14 };
    const thStyle = { background: '#334155', color: '#fff', padding: '12px 15px', textAlign: 'left' };
    const tdStyle = { padding: '12px 15px', borderBottom: '1px solid #e2e8f0', color: '#333', verticalAlign: 'middle' };
    const cardGraphStyle = { width: '100%', background: '#fff', borderRadius: 16, border: '2px solid #e5e7eb', padding: 20, marginBottom: 20, boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)' };
    const kpiCardStyle = { flex: 1, background: '#f8fafc', padding: 15, borderRadius: 8, textAlign: 'center', border: '1px solid #cbd5e1' };

    // Helper de color
    const getColor = (val) => {
        if (val === 0) return '#166534';
        return Math.abs(val) > 2 ? '#dc2626' : '#d97706'; 
    };

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            
            <div style={{width: '100%', maxWidth: 1000}}>
                <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>⬅ Volver al Panel</button>
                
                <h2 style={{ color: '#1e293b' }}>Estadísticas de Precisión</h2>
                <p style={{ color: '#64748b', marginBottom: 20 }}>Análisis técnico del margen de error en las predicciones.</p>

                {loading ? <p>Calculando...</p> : (
                    <>
                        {/* 1. TARJETAS DE RESUMEN (KPIs) */}
                        <div style={{ display: 'flex', gap: 20, marginBottom: 20 }}>
                            
                            <div style={kpiCardStyle}>
                                <h4 style={{margin:0, color:'#64748b'}}>Error Promedio (Sesgo)</h4>
                                <div style={{height: 100, display: 'flex', flexDirection: 'column', justifyContent: 'center'}}>
                                    <span style={{fontSize: 32, fontWeight:'bold', color: stats.mean > 0 ? '#166534' : '#dc2626'}}>
                                        {stats.mean > 0 ? '+' : ''}{stats.mean} Kg
                                    </span>
                                    <div style={{fontSize:11, color:'#94a3b8'}}>Promedio de diferencia.</div>
                                </div>
                            </div>

                            <div style={kpiCardStyle}>
                                <h4 style={{margin:0, color:'#64748b'}}>Desviación Estándar (σ)</h4>
                                <div style={{height: 100, display: 'flex', flexDirection: 'column', justifyContent: 'center'}}>
                                    <span style={{fontSize: 32, fontWeight:'bold', color: '#475569'}}>
                                        ±{stats.stdDev} Kg
                                    </span>
                                    <div style={{fontSize:11, color:'#94a3b8'}}>Volatilidad.</div>
                                </div>
                            </div>

                            {/* KPI MAPE (Gráfica Circular) */}
                            <div style={{...kpiCardStyle, padding: 5, display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
                                <h4 style={{margin:'10px 0 0 0', color:'#64748b'}}>Error % Promedio (MAPE)</h4>
                                <div style={{ height: 120, width: '100%', position: 'relative' }}>
                                    <ResponsiveContainer width="100%" height="100%">
                                        <PieChart>
                                            <Pie
                                                data={[
                                                    { name: 'Error', value: Math.min(stats.mape, 100) }, 
                                                    { name: 'Resto', value: Math.max(0, 100 - stats.mape) }
                                                ]}
                                                cx="50%"
                                                cy="50%"
                                                innerRadius={35}
                                                outerRadius={50}
                                                startAngle={90}
                                                endAngle={-270}
                                                dataKey="value"
                                                stroke="none"
                                            >
                                                <Cell fill={stats.mape < 15 ? '#22c55e' : stats.mape < 30 ? '#f59e0b' : '#ef4444'} />
                                                <Cell fill="#e5e7eb" />
                                            </Pie>
                                            <Tooltip />
                                        </PieChart>
                                    </ResponsiveContainer>
                                    
                                    <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', textAlign: 'center' }}>
                                        <span style={{fontSize: 18, fontWeight: 'bold', color: stats.mape < 15 ? '#166534' : '#7f1d1d'}}>
                                            {stats.mape}%
                                        </span>
                                    </div>
                                </div>
                                <div style={{fontSize:11, color:'#94a3b8', marginBottom: 10}}>Menos de 15% es excelente.</div>
                            </div>
                        </div>

                        {/* 2. GRÁFICA DE ERROR PORCENTUAL (Única gráfica grande) */}
                        <div style={cardGraphStyle}>
                            <h3 style={{textAlign:'center', color:'#555', marginBottom:10}}>Porcentaje de Error por Pedido</h3>
                            <div style={{ height: 300 }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <BarChart data={data} layout="vertical">
                                        <CartesianGrid strokeDasharray="3 3" />
                                        <XAxis type="number" domain={['auto', 'auto']} />
                                        <YAxis dataKey="cliente" type="category" width={100} />
                                        <Tooltip />
                                        <Bar dataKey="errorPct" name="% Error" fill="#82ca9d" barSize={20} />
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>
                        </div>

                        {/* 3. TABLA DE DETALLE */}
                        <h3 style={{marginTop: 30, color: '#334155'}}>Detalle de Datos</h3>
                        <table style={tableStyle}>
                            <thead>
                                <tr>
                                    <th style={thStyle}>Cliente</th>
                                    <th style={thStyle}>Predicho (Kg)</th>
                                    <th style={thStyle}>Real (Kg)</th>
                                    <th style={thStyle}>Dif (Kg)</th>
                                    <th style={thStyle}>Error %</th>
                                </tr>
                            </thead>
                            <tbody>
                                {data.map((item) => (
                                    <tr key={item.id} style={{ background: '#fff' }}>
                                        <td style={tdStyle}>
                                            <div style={{fontWeight:'bold'}}>{item.usuario?.username}</div>
                                            <div style={{fontSize:11, color:'#64748b'}}>{new Date(item.fecha).toLocaleDateString()}</div>
                                        </td>
                                        <td style={tdStyle}>{item.kgPred}</td>
                                        <td style={tdStyle}><strong>{item.kgReal}</strong></td>
                                        <td style={tdStyle}>
                                            <span style={{fontWeight:'bold', color: getColor(item.diffKg)}}>
                                                {item.diffKg > 0 ? '+' : ''}{item.diffKg.toFixed(1)}
                                            </span>
                                        </td>
                                        <td style={tdStyle}>
                                            <span style={{
                                                background: Math.abs(item.errorPct) < 10 ? '#dcfce7' : '#fee2e2',
                                                color: Math.abs(item.errorPct) < 10 ? '#166534' : '#991b1b',
                                                padding: '2px 6px', borderRadius: 4, fontSize: 12, fontWeight: 'bold'
                                            }}>
                                                {item.errorPct}%
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </>
                )}
            </div>
        </div>
    );
}

export default AdminDesviaciones;
