import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';
import { 
    AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer
} from 'recharts';

function AdminPlaneador() {
    // Estado para lo que se ve en pantalla (Filtrado)
    const [viewData, setViewData] = useState({ totalKg: 0, detalle: [], dailyAvg: 0, daysCount: 0 });
    const [chartData, setChartData] = useState([]);
    
    // Estado "Maestro" (Todos los datos originales de la API)
    const [allPredictions, setAllPredictions] = useState([]); 
    
    // Estado para los filtros
    const [fechas, setFechas] = useState({ inicio: '', fin: '' });
    
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchPlanning();
    }, []);

    // Función auxiliar para recalcular todo (KPIs y Gráfica) basándose en una lista dada
    const procesarDatos = (listaPredicciones) => {
        // 1. Calcular Total Kg
        const totalKg = listaPredicciones.reduce((acc, curr) => acc + curr.cantidad, 0);

        // 2. Agrupar para la Gráfica y contar Días Activos
        const grouped = {};
        listaPredicciones.forEach(p => {
            const fechaKey = new Date(p.fecha).toISOString().split('T')[0]; // YYYY-MM-DD
            const label = new Date(p.fecha).toLocaleDateString('es-MX', { month: 'short', day: 'numeric' });
            
            if (!grouped[label]) grouped[label] = 0;
            grouped[label] += p.cantidad;
        });

        // 3. Calcular Promedio Diario
        const activeDays = Object.keys(grouped).length || 1; // Evitar división por cero
        const dailyAvg = activeDays > 0 ? (totalKg / activeDays).toFixed(1) : 0;

        // 4. Preparar datos para Recharts
        const chart = Object.keys(grouped).map(key => ({
            fecha: key,
            kg: grouped[key]
        }));

        // 5. Actualizar vista
        setViewData({
            totalKg,
            detalle: listaPredicciones, // La tabla muestra la lista filtrada
            dailyAvg,
            daysCount: activeDays
        });
        setChartData(chart);
    };

    const fetchPlanning = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await api.get('/predicciones/planeador-inventario', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            // Guardamos la copia maestra
            setAllPredictions(response.data.detalle);
            
            // Procesamos todos los datos inicialmente
            procesarDatos(response.data.detalle);

        } catch (error) {
            console.error("Error cargando planeación", error);
        } finally {
            setLoading(false);
        }
    };

    // Manejador del filtro
    const handleFilter = () => {
        if (!fechas.inicio && !fechas.fin) {
            // Si no hay fechas, restaurar todo
            procesarDatos(allPredictions);
            return;
        }

        const filtrados = allPredictions.filter(p => {
            const fechaP = new Date(p.fecha).toISOString().split('T')[0];
            const inicioValido = fechas.inicio ? fechaP >= fechas.inicio : true;
            const finValido = fechas.fin ? fechaP <= fechas.fin : true;
            return inicioValido && finValido;
        });

        procesarDatos(filtrados);
    };

    // Limpiar filtro
    const clearFilter = () => {
        setFechas({ inicio: '', fin: '' });
        procesarDatos(allPredictions);
    };

    // Estilos
    const containerStyle = { minHeight: '100vh', background: '#f8fafc', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' };
    const statsContainerStyle = { display: 'flex', gap: '20px', flexWrap: 'wrap', width: '100%', maxWidth: 900, marginBottom: 30, justifyContent: 'center' };
    const kpiCardStyle = { background: '#fff', padding: 30, borderRadius: 16, boxShadow: '0 4px 6px rgba(0,0,0,0.05)', textAlign: 'center', border: '1px solid #e2e8f0', flex: '1 1 300px', minWidth: 250 };
    const graphCardStyle = { background: '#fff', padding: 20, borderRadius: 16, boxShadow: '0 4px 6px rgba(0,0,0,0.05)', border: '1px solid #e2e8f0', width: '100%', maxWidth: 900 };
    const filterStyle = { background: '#fff', padding: '15px', borderRadius: 12, marginBottom: 20, display: 'flex', gap: 10, alignItems: 'center', border: '1px solid #cbd5e1', flexWrap: 'wrap' };

    return (
        <div style={containerStyle}>
            <div style={{width: '100%', maxWidth: 1000}}>
                <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>⬅ Volver al Panel</button>
                
                <h2 style={{ color: '#1e293b', textAlign: 'center' }}>Planeador de Producción</h2>
                
                {/* --- BARRA DE FILTROS --- */}
                <div style={{display:'flex', justifyContent:'center'}}>
                    <div style={filterStyle}>
                        <label style={{fontSize:14, color:'#64748b'}}>Desde:</label>
                        <input 
                            type="date" 
                            value={fechas.inicio}
                            onChange={(e) => setFechas({...fechas, inicio: e.target.value})}
                            style={{padding: 8, borderRadius: 6, border:'1px solid #cbd5e1'}}
                        />
                        
                        <label style={{fontSize:14, color:'#64748b', marginLeft:10}}>Hasta:</label>
                        <input 
                            type="date" 
                            value={fechas.fin}
                            onChange={(e) => setFechas({...fechas, fin: e.target.value})}
                            style={{padding: 8, borderRadius: 6, border:'1px solid #cbd5e1'}}
                        />

                        <button 
                            onClick={handleFilter}
                            style={{marginLeft: 10, background: '#7c3aed', color:'white', border:'none', padding:'8px 16px', borderRadius:6, cursor:'pointer'}}
                        >
                            Filtrar
                        </button>
                        
                        {(fechas.inicio || fechas.fin) && (
                            <button 
                                onClick={clearFilter}
                                style={{background: 'transparent', color:'#64748b', border:'1px solid #cbd5e1', padding:'8px 16px', borderRadius:6, cursor:'pointer'}}
                            >
                                Limpiar
                            </button>
                        )}
                    </div>
                </div>

                {loading ? <p style={{textAlign:'center'}}>Calculando demanda futura...</p> : (
                    <div style={{display:'flex', flexDirection:'column', alignItems:'center'}}>
                        
                        {/* SECCIÓN DE KPIS */}
                        <div style={statsContainerStyle}>
                            <div style={kpiCardStyle}>
                                <h3 style={{margin:0, color:'#64748b', fontSize:14, textTransform:'uppercase', letterSpacing:1}}>Necesidad (Selección)</h3>
                                <div style={{fontSize: 48, fontWeight:'bold', color: '#7c3aed', margin:'10px 0'}}>
                                    {viewData.totalKg} <span style={{fontSize:18, color:'#94a3b8'}}>Kg</span>
                                </div>
                                <div style={{fontSize:12, color:'#64748b'}}>
                                    En {viewData.detalle.length} predicciones filtradas.
                                </div>
                            </div>

                            <div style={kpiCardStyle}>
                                <h3 style={{margin:0, color:'#64748b', fontSize:14, textTransform:'uppercase', letterSpacing:1}}>Promedio Diario</h3>
                                <div style={{fontSize: 48, fontWeight:'bold', color: '#0ea5e9', margin:'10px 0'}}>
                                    {viewData.dailyAvg} <span style={{fontSize:18, color:'#94a3b8'}}>Kg/día</span>
                                </div>
                                <div style={{fontSize:12, color:'#64748b'}}>
                                    Basado en los {viewData.daysCount} días del rango.
                                </div>
                            </div>
                        </div>

                        {/* GRÁFICA DE DEMANDA (Se actualiza sola con chartData) */}
                        <div style={graphCardStyle}>
                            <h3 style={{color:'#334155', marginBottom:20, textAlign:'center'}}>
                                Cronograma {fechas.inicio ? '(Filtrado)' : '(Completo)'}
                            </h3>
                            <div style={{ height: 300 }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <AreaChart data={chartData} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                                        <defs>
                                            <linearGradient id="colorKg" x1="0" y1="0" x2="0" y2="1">
                                                <stop offset="5%" stopColor="#8b5cf6" stopOpacity={0.8}/>
                                                <stop offset="95%" stopColor="#8b5cf6" stopOpacity={0}/>
                                            </linearGradient>
                                        </defs>
                                        <XAxis dataKey="fecha" />
                                        <YAxis />
                                        <CartesianGrid strokeDasharray="3 3" vertical={false} />
                                        <Tooltip />
                                        <Area type="monotone" dataKey="kg" stroke="#7c3aed" fillOpacity={1} fill="url(#colorKg)" name="Demanda (Kg)" />
                                    </AreaChart>
                                </ResponsiveContainer>
                            </div>
                        </div>

                        {/* TABLA RESUMEN */}
                        <div style={{marginTop: 30, width: '100%', maxWidth: 900}}>
                            <h4 style={{color: '#475569'}}>Desglose por Cliente</h4>
                            {viewData.detalle.length === 0 ? (
                                <p style={{textAlign:'center', color:'#94a3b8', fontStyle:'italic'}}>No hay predicciones en este rango de fechas.</p>
                            ) : (
                                <table style={{width: '100%', borderCollapse: 'collapse', background: '#fff', borderRadius: 8, overflow: 'hidden'}}>
                                    <thead style={{background: '#f1f5f9'}}>
                                        <tr>
                                            <th style={{padding:12, textAlign:'left', color:'#475569'}}>Fecha Estimada</th>
                                            <th style={{padding:12, textAlign:'left', color:'#475569'}}>Cliente</th>
                                            <th style={{padding:12, textAlign:'right', color:'#475569'}}>Requerimiento</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {viewData.detalle.map(p => (
                                            <tr key={p.id} style={{borderBottom: '1px solid #e2e8f0'}}>
                                                <td style={{padding:12}}>{new Date(p.fecha).toLocaleDateString()}</td>
                                                <td style={{padding:12}}>
                                                    <strong>{p.usuario?.username}</strong>
                                                    <span style={{fontSize:11, color:'#64748b', marginLeft:5}}>({p.usuario?.perfil?.restaurant || '-'})</span>
                                                </td>
                                                <td style={{padding:12, textAlign:'right', fontWeight:'bold', color:'#7c3aed'}}>
                                                    {p.cantidad} Kg
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            )}
                        </div>

                    </div>
                )}
            </div>
        </div>
    );
}

export default AdminPlaneador;
