import React, { useState, useEffect } from 'react';
import api from '../../api/api';
import { useNavigate } from 'react-router-dom';

function AdminClientes() {
    const [clientes, setClientes] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchClientes();
    }, []);

    const fetchClientes = async () => {
        try {
            const token = localStorage.getItem('token');
            // El endpoint /users trae todos los usuarios con sus relaciones (perfil, etc.)
            const response = await api.get('/users', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            // Opcional: Filtramos para no ver a otros Admins, solo Clientes
            // Si quieres ver todos, quita el .filter
            const soloClientes = response.data.filter(user => user.rol === 'Cliente');
            
            setClientes(soloClientes);
        } catch (error) {
            console.error("Error cargando clientes", error);
        } finally {
            setLoading(false);
        }
    };

    // Estilos (Consistentes con el resto del panel admin)
    const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: 20, background: '#fff', borderRadius: 8, overflow: 'hidden', fontSize: 14 };
    const thStyle = { background: '#334155', color: '#fff', padding: '12px 15px', textAlign: 'left' };
    const tdStyle = { padding: '12px 15px', borderBottom: '1px solid #e2e8f0', color: '#333', verticalAlign: 'middle' };

    return (
        <div style={{ minHeight: '100vh', background: '#f1f5f9', padding: 20 }}>
            <button onClick={() => navigate('/admin')} style={{ marginBottom: 20, padding: '10px 20px', cursor: 'pointer' }}>‚¨Ö Volver al Panel</button>
            
            <h2 style={{ color: '#1e293b' }}>Directorio de Clientes</h2>

            {loading ? <p>Cargando...</p> : (
                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th style={thStyle}>Usuario</th>
                            <th style={thStyle}>Restaurante (Perfil)</th>
                            <th style={thStyle}>Contacto</th>
                            <th style={thStyle}>Precio Acordado</th>
                            <th style={thStyle}>Registro</th>
                        </tr>
                    </thead>
                    <tbody>
                        {clientes.map(user => (
                            <tr key={user.id} style={{ background: '#fff' }}>
                                {/* 1. Informaci√≥n de Cuenta */}
                                <td style={tdStyle}>
                                    <div style={{fontWeight: 'bold', color: '#0f172a'}}>{user.username}</div>
                                    <div style={{fontSize: 11, color: '#64748b'}}>ID: {user.id.split('-')[0]}...</div>
                                </td>

                                {/* 2. Informaci√≥n del Negocio (Viene del Perfil) */}
                                <td style={tdStyle}>
                                    {user.perfil ? (
                                        <>
                                            <div style={{fontWeight: 'bold', color: '#78549b'}}>{user.perfil.restaurant}</div>
                                            <div style={{fontSize: 12}}>{user.perfil.firstName} {user.perfil.lastName}</div>
                                        </>
                                    ) : (
                                        <span style={{color: '#94a3b8', fontStyle: 'italic'}}>Sin perfil completado</span>
                                    )}
                                </td>

                                {/* 3. Direcci√≥n / Email */}
                                <td style={tdStyle}>
                                    <div>üìß {user.email}</div>
                                    {user.perfil?.direction && (
                                        <div style={{fontSize: 11, color: '#666', marginTop: 4}}>üìç {user.perfil.direction}</div>
                                    )}
                                </td>

                                {/* 4. Precio (Importante para el Admin) */}
                                <td style={tdStyle}>
                                    {user.perfil?.precioAcordado ? (
                                        <span style={{background: '#dcfce7', color: '#166534', padding: '4px 8px', borderRadius: 10, fontWeight: 'bold'}}>
                                            ${user.perfil.precioAcordado} / Kg
                                        </span>
                                    ) : (
                                        <span style={{color: '#94a3b8'}}>$0.00</span>
                                    )}
                                </td>

                                {/* 5. Fecha Registro */}
                                <td style={tdStyle}>
                                    {new Date(user.createdAt).toLocaleDateString()}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
            
            {!loading && clientes.length === 0 && (
                <p style={{textAlign: 'center', marginTop: 20, color: '#666'}}>No se encontraron clientes registrados.</p>
            )}
        </div>
    );
}

export default AdminClientes;
